import React from "react";
import AwesomeAlert from 'react-native-awesome-alerts';

import { 
    ActivityIndicator,
    AsyncStorage,
    FlatList,
    Dimensions,
    StyleSheet, 
    ScrollView,
    TouchableWithoutFeedback,
    TouchableOpacity 
} from "react-native";

import { 
    Block, 
    Input, 
    theme, 
    Button, 
} from "galio-framework";

import { 
    Autocomplete,
    WorkpackList, 
    GradientHeader,
    DetailMVList, 
    Filter, 
    ListChecked ,
    LoadMore ,
    Select,
    SelectMultiple,
    Icon,
    Text,
    Divider,
} from "../../../components";

import {Camera} from 'expo-camera';
let camera = Camera;

import { nowTheme, detail_mrir, example_workpack, config } from "../../../constants";
import { element } from "prop-types";
const { height, width } = Dimensions.get("screen");

class FUSurveyorDetail extends React.Component {

    _isMounted = false;

    constructor() {
        super();
        // console.log();

        this.state = {
          listMaster: [],
          listDetail: [],
          listSubmit: [],
          listItems: [],

          showMaster: false,
          progress: 0,
          confirmAlert: false,
          categorySelected: '',

          isLoading: false,
          isLoadData: false,
          loadButton: false,
          dataLogin: [],
          filter: '',
          category_template: '',
          menu: '',
          filterBy: ['Workpack_No', 'Template'],
          id: 0,
          item: [],
          id_master: 0,

          startCamera: false,
          typeCamera: Camera.Constants.Type.back,
          capturedImage: false,
          photoName: '',

          workpack_no: '',
          Unique_No: {},
          Fitter_Code: [],
          Tack_Weld_ID: [],
          WPS: [],
          remarks: '',
          data_workpack: {},
        };
    }

    async componentDidMount() {
        this._isMounted = true;

        var data_user = await AsyncStorage.getItem('data_user');
        var data_user = JSON.parse(data_user);

        var data_login = {
            project: data_user.project_id,
            api: data_user.api_key,
            id_user: data_user.id_user,
        }

        this.setState({
            dataLogin: data_login,
            item: this.props.route.params.item,
            menu: this.props.route.params.menu,
            data_workpack: this.props.route.params.data_workpack,
            // isLoading: false,
            progress: this.props.route.params.item.progress,
            // id_master: this.props.route.params.id_workpack_master,
            // workpack_no: this.props.route.params.workpack_no,
        });

        // console.log(this.state.data_workpack);

        return () => { isMounted = false };
    }

    readData = () => {

        this.setState({isLoading: true});

        var project = this.state.dataLogin.project;
        var api     = this.state.dataLogin.api;
        var id      = this.state.id;
        var menu    = this.state.menu;

        var filter = id + 'xx';

        var url = config.config.url + '/api/wp/list/' + menu + '/' + project + '/0/' + filter;

        console.log(url);
        console.log(api);

        fetch(url, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json',
                "Accept": "application/json",
                'Authorization': api,
            }),
        })
        .then((response) => response.text())
        .then((data) =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
                list_data = data + '}';
            }

            // console.log(list_data);

            list_data = JSON.parse(list_data);

            this.setState({
                listMaster: list_data.results_master,
                listDetail: list_data.results_detail,
                isLoading: false,
            });

            console.log(this.state.listMaster);

        }).catch((error) => {
            console.error(error);
        });
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    render() {
        if(this.state.startCamera){
            return this.renderCamera()
        } else {
            return (
                <Block flex center style={styles.home}>
                    {this.state.isLoading ? (
                        <Block style={{ flex: 1, justifyContent: 'center', alignItems: 'center'  }}>
                            <ActivityIndicator size="large" color={nowTheme.COLORS.PRIMARY} />
                        </Block>
                    ) : (
                        <Block flex center style={styles.home}>
                            { this.renderHeader() }
                            { this.renderArticles() }
                            { this.renderAlert() }
                        </Block>
                    )}
                </Block>
            );
        }
    }

    renderHeader = () => {
        return(
            <GradientHeader 
              datauser={''}
              title={'Surveyor'}
              menu={''}
              back={true}
              gradientColor={nowTheme.COLORS.GRADIENT}
              borderRadius={5}
              profile={false}
              headerHeight={10}
            />
        )
    }

    renderArticles = () => {

        return (
            <Block flex>
                <ScrollView
                    showsVerticalScrollIndicator={false}
                    keyboardShouldPersistTaps='handled'
                    contentContainerStyle={''}
                >
                    <Block>
                        <Block style={{paddingTop: 10}}>
                            <Block card style={{width: width / 1.1, backgroundColor: nowTheme.COLORS.WHITE, paddingHorizontal: 20, paddingVertical: 10}}>

                                <Block style={{paddingTop: 10}}>
                                    <Text
                                        title={'DATA DETAIL'}
                                        bold
