    onSubmit = async() => {
        var progress    = this.state.progress;

        var data = [];

        // ================= FITTER CODE ===============
        var fitter_code = [];

        this.state.Fitter_Code.forEach(element => {
            fitter_code.push(element.id);
        });
        // =============================================

        // ================ TACK WELD ID ===============
        var welder_code = [];

        this.state.Tack_Weld_ID.forEach(element => {
            welder_code.push(element.id);
        });
        // =============================================

        // =================== WPS =====================
        var wps = [];

        this.state.WPS.forEach(element => {
            wps.push(element.id);
        });
        // =============================================

        var api         = this.state.dataLogin.api;

        data.push({
            'fitter_code': fitter_code,
            'welder_code': welder_code,
            'wps': wps,
        })

        var id_master   = this.state.data_workpack.id;
        // var id_user     = this.state.dataLogin.id_user;
        // var api         = this.state.dataLogin.api;

        let options = {
            method: 'POST',
            headers: new Headers({
                'Authorization': api,
                'Content-Type': 'application/json'
            }),
            body: JSON.stringify({
                // approved_by: id_user,
                submission: data,
                progress: progress,
                menu: this.state.menu,
                id: this.state.item.id,
                id_master: id_master, 
                data_user: this.state.dataLogin, 
            })
        };

        var url = config.config.url + '/api/wp/surveyor_submit_process';

        console.log(url)
        console.log(api)
        console.log(options)

        await fetch(url, options)
        .then(response => response.text())
        .then(data => {

            console.log(data);

            this.setState({
                successAlert: true,
                listApproval: []
            });

            // this.props.navigation.navigate('Detail MV');

        })
        .catch(error => {
            console.error(error);
        });
    }

