import React from 'react';
import { withNavigation } from '@react-navigation/compat';
import PropTypes from 'prop-types';

import { Col, Row, Grid } from "react-native-easy-grid";

import { 
    AsyncStorage,
    StyleSheet, 
    Image, 
    TouchableWithoutFeedback 
} from 'react-native';

import { 
  Block, 
  Checkbox,
  Text, 
  theme, 
  Icon 
} from 'galio-framework';

import ContentApproval from './ContentApproval';

import { nowTheme } from '../constants';

class ApprovalVisualList extends React.Component {

  constructor() {
    super();

    this.state = {
      isChecked: false,
      isShow: false,
    };
  }

  checkboxChecked = (id) => {
    this.setState({isChecked: !this.state.isChecked});

    var checked = !this.state.isChecked;

    this.props.checkboxChange(checked, id);

  }

  render() {
    const {
      navigation,
      item,
      horizontal,
      full,
      style,
      ctaColor,
      imageStyle,
      ctaRight,
      titleStyle
    } = this.props;

    const imageStyles = [full ? styles.fullImage : styles.horizontalImage, imageStyle];
    const titleStyles = [styles.cardTitle, titleStyle];
    const cardContainer = [styles.card, styles.shadow, style];
    const cardContainerActive = [styles.card, styles.cardActive, styles.shadow, style];
    const imgContainer = [
      styles.imageContainer,
      horizontal ? styles.horizontalStyles : styles.verticalStyles,
      styles.shadow
    ];

    return (
      <Block row={horizontal} card flex style={ this.state.isChecked == true ? cardContainerActive : cardContainer }>
        <TouchableWithoutFeedback
          onPress={() => this.checkboxChecked(item.id)}
        >
          <Block flex space="between" style={styles.cardDescription}>
            <Block flex>
              <Block flex>

                {this.state.isChecked == true ? (
                  <Block right style={{marginTop: -10, marginRight: -5}} >
                    <Text>
                      <Icon
                        size={20}
                        color={nowTheme.COLORS.PRIMARY}
                        name="check-circle"
                        family="Feather"
                      />
                    </Text>
                  </Block>
                ) : null}

                <Grid>                  
                  <Col>
                    <Row>
                      <Block flex style={{
                          paddingBottom: 10,
                        }}>
                          <Text
                            style={{ 
                                fontFamily: 'montserrat-bold', 
                                textAlign: 'left',
                            }}
                            size={15}
                            color={nowTheme.COLORS.SECONDARY}
                          >
                              {item.weld_map_no}
                          </Text>
                      </Block>
                    </Row>

                    <Row>
                      {item.weld_map_no ? (
                        <ContentApproval 
                          title='Weld Map No'
                          content={item.weld_map_no}
                        />
                      ) : (
                        <Block />
                      )}
                    </Row>

                    <Row>
                      {item.joint_no ? (
                        <ContentApproval 
                          title='Joint No'
                          content={item.joint_no}
                        />
                      ) : (
                        <Block />
                      )}
                    </Row>

                    <Row>
                      {item.pwht ? (
                        <ContentApproval 
                          title='PWHT'
                          content={item.pwht}
                        />
                      ) : (
                        <Block />
                      )}
                    </Row>

                    <Row>
                      {item.cons_lot_no ? (
                        <ContentApproval 
                          title='Consumable / Lot No'
                          content={item.cons_lot_no}
                        />
                      ) : (
                        <Block />
                      )}
                    </Row>

                    <TouchableWithoutFeedback 
                      onPress={() => this.setState({isShow: !this.state.isShow})}
                    >
                      <Block 
                        center
                        style={{
                          paddingVertical: 5
                        }}
                      >
                        <Icon
                          size={30}
                          color="#ADB5BD"
                          name= { (this.state.isShow == false) ? "arrow-down-circle" : "arrow-up-circle"}
                          family="Feather"
                        />
                      </Block>
                    </TouchableWithoutFeedback>

                    { this.state.isShow == true ? (
                    <Block>
                      <Row>
                        {item.length_of_weld ? (
                          <ContentApproval 
                            title='Length of Weld'
                            content={item.length_of_weld}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.length ? (
                          <ContentApproval 
                            title='Length'
                            content={item.length}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.location ? (
                          <ContentApproval 
                            title='Location'
                            content={item.location}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.thk ? (
                          <ContentApproval 
                            title='Thk'
                            content={item.thk}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.dia ? (
                          <ContentApproval 
                            title='Dia (mm)'
                            content={item.dia}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.remarks ? (
                          <ContentApproval 
                            title='Remarks'
                            content={item.remarks}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.ndt_req ? (
                          <ContentApproval 
                            title='NDT Requirement'
                            content={item.ndt_req}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.process_rh ? (
                          <ContentApproval 
                            title='Process RH'
                            content={item.process_rh}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.welder_ref_rh ? (
                          <ContentApproval 
                            title='Welder Ref RH'
