import React from 'react';
import { withNavigation } from '@react-navigation/compat';
import PropTypes from 'prop-types';

import { Col, Row, Grid } from "react-native-easy-grid";

import { 
    AsyncStorage,
    StyleSheet, 
    Image, 
    TouchableWithoutFeedback 
} from 'react-native';

import { 
  Block, 
  Checkbox,
  Text, 
  theme, 
  Icon 
} from 'galio-framework';

import ContentApproval from './ContentApproval';

import { nowTheme } from '../constants';

class ApprovalMVList extends React.Component {

  constructor() {
    super();

    this.state = {
      isChecked: false,
      isShow: false,
    };
  }

  checkboxChecked = (id) => {
    this.setState({isChecked: !this.state.isChecked});

    var checked = !this.state.isChecked;

    this.props.checkboxChange(checked, id);

  }

  render() {
    const {
      navigation,
      item,
      horizontal,
      full,
      style,
      ctaColor,
      imageStyle,
      ctaRight,
      titleStyle
    } = this.props;

    const imageStyles = [full ? styles.fullImage : styles.horizontalImage, imageStyle];
    const titleStyles = [styles.cardTitle, titleStyle];
    const cardContainer = [styles.card, styles.shadow, style];
    const cardContainerActive = [styles.card, styles.cardActive, styles.shadow, style];
    const imgContainer = [
      styles.imageContainer,
      horizontal ? styles.horizontalStyles : styles.verticalStyles,
      styles.shadow
    ];

    return (
      <Block row={horizontal} card flex style={ this.state.isChecked == true ? cardContainerActive : cardContainer }>
        <TouchableWithoutFeedback
          onPress={() => this.checkboxChecked(item.verid_detail)}
        >
          <Block flex space="between" style={styles.cardDescription}>
            <Block flex>
              <Block flex>

                {this.state.isChecked == true ? (
                  <Block right style={{marginTop: -10, marginRight: -5}} >
                    <Text>
                      <Icon
                        size={20}
                        color={nowTheme.COLORS.PRIMARY}
                        name="check-circle"
                        family="Feather"
                      />
                    </Text>
                  </Block>
                ) : null}

                <Grid>                  
                  <Col>
                    <Row>
                      <Block style={{
                        paddingBottom: 10,
                      }}>
                        <Text
                            style={{ 
                                fontFamily: 'montserrat-bold', 
                            }}
                            size={15}
                            color={nowTheme.COLORS.SECONDARY}
                        >
                          {item.assembly_drawing_no}
                        </Text>
                      </Block>
                    </Row>

                    <Row>
                      {item.batch_no ? (
                        <ContentApproval 
                          title='Batch No'
                          content={item.batch_no}
                        />
                      ) : (
                        <Block />
                      )}
                    </Row>

                    <Row>
                      {item.assembly_drawing_no ? (
                        <ContentApproval 
                          title='Assembly Drawing No'
                          content={item.assembly_drawing_no}
                        />
                      ) : (
                        <Block />
                      )}
                    </Row>

                    <Row>
                      {item.piece_mark_no ? (
                        <ContentApproval 
                          title='Piece Mark No.'
                          content={item.piece_mark_no}
                        />
                      ) : (
                        <Block />
                      )}
                    </Row>

                    <TouchableWithoutFeedback 
                      onPress={() => this.setState({isShow: !this.state.isShow})}
                    >
                      <Block 
                        center
                        style={{
                          paddingVertical: 5
                        }}
                      >
                        <Icon
                          size={30}
                          color="#ADB5BD"
                          name= { (this.state.isShow == false) ? "arrow-down-circle" : "arrow-up-circle"}
                          family="Feather"
                        />
                      </Block>
                    </TouchableWithoutFeedback>

                    { this.state.isShow == true ? (
                    <Block>
                      <Row>
                        {item.can_number ? (
                          <ContentApproval 
                            title='Can Number'
                            content={item.can_number}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.uniq_no ? (
                          <ContentApproval 
                            title='Unique No'
                            content={item.uniq_no}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.spec_grade ? (
                          <ContentApproval 
                            title='Spec / Grade'
                            content={item.spec_grade}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.heat_or_series_no ? (
                          <ContentApproval 
                            title='Heat No.'
                            content={item.heat_or_series_no}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.diameter_in ? (
                          <ContentApproval 
                            title='Width / OD'
                            content={item.diameter_in}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.thk ? (
                          <ContentApproval 
                            title='Thk'
                            content={item.thk}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.material_description ? (
                          <ContentApproval 
                            title='Material Description'
                            content={item.material_description}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.mrir ? (
                          <ContentApproval 
                            title='MRIR'
                            content={item.mrir}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.strain_dt ? (
                          <ContentApproval 
                            title='D / T'
                            content={item.strain_dt}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.strain_yes_no ? (
                          <ContentApproval 
                            title='Yes / No'
                            content={item.strain_yes_no}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                      <Row>
                        {item.through_thickness ? (
                          <ContentApproval 
                            title='Through Thickness'
                            content={item.through_thickness}
                          />
                        ) : (
                          <Block />
                        )}
                      </Row>

                    </Block>
                    ) : <Block />}

                  </Col>

                </Grid>
              </Block>
            </Block>

          </Block>
        </TouchableWithoutFeedback>
      </Block>
    );
  }
}

ApprovalMVList.propTypes = {
  item: PropTypes.object,
  horizontal: PropTypes.bool,
  full: PropTypes.bool,
  ctaColor: PropTypes.string,
  imageStyle: PropTypes.any,
  ctaRight: PropTypes.bool,
  titleStyle: PropTypes.any,
  textBodyStyle: PropTypes.any
};

const styles = StyleSheet.create({
  card: {
    backgroundColor: theme.COLORS.WHITE,
    marginVertical: theme.SIZES.BASE,
    borderWidth: 0,
    minHeight: 80,
    marginBottom: 4
  },
  cardActive: {
    borderWidth: 2,
    borderColor: nowTheme.COLORS.PRIMARY,
  },
  cardTitle: {
    paddingHorizontal: 9,
