import React from "react";

import { 
    ActivityIndicator,
    AsyncStorage,
    FlatList,
    Dimensions,
    StyleSheet, 
    ScrollView,
    TouchableWithoutFeedback 
} from "react-native";

import { 
    Block, 
    Input, 
    theme, 
} from "galio-framework";

import { 
    Autocomplete,
    WorkpackList, 
    DetailMVList, 
    Button, 
    Filter, 
    ListChecked ,
    LoadMore ,
    Icon,
    Text,
} from "../../components";

import { nowTheme, detail_mrir, example_workpack, config } from "../../constants";
const { height, width } = Dimensions.get("screen");

class WorkpackPiecemark extends React.Component {

    _isMounted = false;

    constructor() {
        super();
        // console.log();

        this.state = {
          listMaster: [],
          listDetail: [],
          listSubmit: [],
          listPiecemark: [],

          showMaster: false,

          isLoading: false,
          isLoadData: false,
          loadButton: false,
          dataLogin: [],
          filter: '',
          category_template: '',
          menu: '',
          filterBy: ['Workpack_No', 'Template'],
          id: 0,

          Workpack_No: {},
        };
    }

    async componentDidMount() {
        this._isMounted = true;

        var data_user = await AsyncStorage.getItem('data_user');
        var data_user = JSON.parse(data_user);

        var data_login = {
            project: data_user.project_id,
            api: data_user.api_key,
        }

        this.setState({
            dataLogin: data_login,
            id: this.props.route.params.id,
            menu: this.props.route.params.menu,
        });

        // console.log(this.props);
        // this.readData();

        return () => { isMounted = false };
    }

    readData = () => {

        this.setState({isLoading: true});

        var project = this.state.dataLogin.project;
        var api     = this.state.dataLogin.api;
        var id      = this.state.id;
        var menu    = this.state.menu;

        var filter = id + 'xx';

        var url = config.config.url + '/api/wp/list/' + menu + '/' + project + '/0/' + filter;

        console.log(url);
        console.log(api);

        fetch(url, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json',
                "Accept": "application/json",
                'Authorization': api,
            }),
        })
        .then((response) => response.text())
        .then((data) =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
                list_data = data + '}';
            }

            // console.log(list_data);

            list_data = JSON.parse(list_data);

            this.setState({
                listMaster: list_data.results_master,
                listDetail: list_data.results_detail,
                isLoading: false,
            });

            console.log(this.state.listMaster);

        }).catch((error) => {
            console.error(error);
        });
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    render() {
        return (
            <Block flex center style={styles.home}>
                {this.state.isLoading ? (
                    <Block style={{ flex: 1, justifyContent: 'center', alignItems: 'center'  }}>
                        <ActivityIndicator size="large" color={nowTheme.COLORS.PRIMARY} />
                    </Block>
                ) : (
                    <Block flex center style={styles.home}>
                        { this.renderFilter(this.state.listMaster) }
                        { this.renderArticles() }
                        { this.renderFooter() }
                    </Block>
                )}
            </Block>
        );
    }

    renderArticles = () => {

        return (
            <Block flex center>
                <ScrollView
                    showsVerticalScrollIndicator={false}
                    keyboardShouldPersistTaps='handled'
                    contentContainerStyle={''}
                >                    
                    { 
                        // this.state.listDetail.map((item, index) => {
                        example_workpack.map((item, index) => {
                            return this.renderContent(item,index) 
                        })
                    }
                </ScrollView>
            </Block>
        );
    };

    renderFilter = (item) => {
        return(
            <Block center>
                <Block>
                    <Block style={{paddingVertical: 10}}>
                        <Block 
                            card  
                            style={{
                                width: width / 1.1, 
                                paddingVertical: 10, 
                                paddingHorizontal: 10, 
                                backgroundColor: nowTheme.COLORS.WHITE
                            }}
                        >

                            <Block>
                                <Block>
                                    <Block style={{paddingHorizontal: 10, paddingBottom: 10}}>
                                        <Block center>
                                            <Text
                                                title={'Workpack Detail'}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>
                                    </Block>
                                </Block>
                            </Block>

                            <Block row style={{paddingBottom: 5}}>
                                <Block style={{width: '50%'}}>
                                    <Block style={{paddingHorizontal: 10}}>
                                        <Block>
                                            <Text
                                                title={'Workpack No'}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>
                                    </Block>
                                </Block>

                                <Block style={{width: '50%'}}>
                                    <Block style={{paddingHorizontal: 10}}>
                                        <Text
                                            title={item.workpack_no}
                                            bold
                                            color={nowTheme.COLORS.GRAY}
                                        />
                                    </Block>
                                </Block>
                            </Block>

                            <Block >
                                <Block>
                                    <Block style={{paddingHorizontal: 10, paddingBottom: 5}}>
                                        <Block center>
                                            <TouchableWithoutFeedback onPress={() => this.setState({showMaster: !this.state.showMaster})}>
                                                <Icon
                                                    size={25}
                                                    color={nowTheme.COLORS.GRAY}
                                                    name={ this.state.showMaster ? "arrow-up-circle" : "arrow-down-circle"}
                                                    family="Feather"
                                                />
                                            </TouchableWithoutFeedback>
                                        </Block>
                                    </Block>
                                </Block>
                            </Block>

                            {
                                this.state.showMaster ? (
                                    <Block>

                                        <Block row style={{paddingBottom: 5}}>
                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={'Drawing No'}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={item.drawing_no}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>
                                        </Block>

                                        <Block row style={{paddingBottom: 5}}>
                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={'Module'}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={item.module}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>
                                        </Block>

                                        <Block row style={{paddingBottom: 5}}>
                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={'Type of Module'}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={item.type_of_module}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>
                                        </Block>

                                        <Block row style={{paddingBottom: 5}}>
                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={'Deck Elevation'}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={item.deck_elevation}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>
                                        </Block>

                                        <Block row style={{paddingBottom: 5}}>
                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={'Phase'}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={item.phase}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>
                                        </Block>

                                        <Block row style={{paddingBottom: 5}}>
                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={'Description Assy Code'}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={item.description_assy_code}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>
                                        </Block>

                                        <Block row style={{paddingBottom: 5}}>
                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={'Description'}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={item.description}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>
                                        </Block>

                                        <Block row style={{paddingBottom: 5}}>
                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={'Job No'}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={item.job_no}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>
                                        </Block>

                                        <Block row style={{paddingBottom: 5}}>
                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={'Job Description'}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{width: '50%'}}>
                                                <Block style={{paddingHorizontal: 10}}>
                                                    <Text
                                                        title={item.job_description}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>
                                        </Block>
                                    </Block>
                                ) : <Block />
                            }

                        </Block>
                    </Block>
                </Block>
            </Block>
        )
    }

    renderContent = (item, index) => {
        return(
            <ListChecked 
                itemChecked={this.itemChecked}
                item={item}
                key={index}
                status={item.selected}
            />
        )
    }

    renderFooter = () => {
        return(
            <Block>
                <Block>
                    <Block>
                        <Block card style={{width: width, backgroundColor: nowTheme.COLORS.WHITE}}>

                            <Block style={{paddingHorizontal: 15, paddingTop: 10}}>
                                <Text
                                    title={'Total : ' + this.state.listSubmit.length }
                                    bold
                                    color={nowTheme.COLORS.GRAY}
                                />
                            </Block>

                            <Block center>
                                <Button 
                                    style={{width: width / 1.1}}
                                    onPress={() => this.submitClicked()}
                                >
                                    <Text
                                        title={'Submit'}
                                        size={17}
                                        bold
                                        color={nowTheme.COLORS.WHITE}
                                    />
                                </Button>
                            </Block>

                        </Block>
                    </Block>
                </Block>
            </Block>
        )
    }

    submitClicked = () => {
        var id_master       = this.state.id;
        var list_data       = this.state.listSubmit;
        var list_piecemark  = this.state.listPiecemark;
        var workpack_no     = this.state.listMaster.workpack_no;
        var menu            = this.state.menu;

        this.props.navigation.navigate('Workpack Piecemark Submission', {
            id: list_data,
            id_master: id_master,
            title: 'Piecemark Submission',
            piecemark: list_piecemark,
            workpack_no: workpack_no,
            menu: menu,
        })
    }

    itemChecked = (checked, item) => {
        var id          = item.id;
        var piecemark   = item.piecemark;

        let submit = this.state.listSubmit;
        let piecemark_list = this.state.listPiecemark;

        if(checked == true){
            submit.push(id);
            piecemark_list.push(piecemark);
        } else {
            for(var i=0; i < submit.length; i++){
                if(submit[i] == id){
                    submit.splice(i, 1);
                    piecemark_list.splice(i, 1);
                }
            }
        }

        this.setState({
            listSubmit: submit,
            listPiecemark: piecemark_list,
        });

        // console.log(this.state.listPiecemark);

    }

    selectedItem = (item, category) => {
        this.setState({[category] : item});
    }

    textChanged = (val, props) => {
        var search = val;
        var category = props;
        var project = this.state.dataLogin.project;
        var api = this.state.dataLogin.api;

        if(search){
          var url = '';

          if(category == 'Workpack_No'){
            url = config.config.url + '/api/wp/list/' + project + '/' + search;
          }

          console.log(url);
          console.log(api);

          fetch(url, {
              method: 'GET',
              headers: new Headers({
                  'Authorization': api
              }),
          })
          .then((response) => response.text())
          .then(data =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
              list_data = data + '}';
            }

            list_data = JSON.parse(list_data);

            this.setState({
              listItems: list_data.results,
              categorySelected: category,
            })

          }).catch((error) => {
            console.error(error);
          });

        } else {
          this.setState({
            listItems: []
          });
        }

    }

    searchClicked = () => {
        var workpack_id = this.state.Workpack_No.id;
        var workpack_no = this.state.Workpack_No.name;
        var phase = this.state.Workpack_No.phase;

        if(phase == 'PF'){
            this.props.navigation.navigate('Workpack Piecemark', {
                title: workpack_no,
                id: workpack_id,
            })
        } else {
            this.props.navigation.navigate('Workpack Joint', {
                title: workpack_no,
                id: workpack_id,
            })
        }

    }
}

