import React from "react";

import { withNavigation } from '@react-navigation/compat';
import PropTypes from 'prop-types';

import { 
  ActivityIndicator,
  AsyncStorage, 
  Dimensions,
  FlatList,
  Keyboard,
  StyleSheet, 
  ScrollView,
  TouchableWithoutFeedback,
  View, 
} from "react-native";

import { Block, theme, Text } from "galio-framework";

import { Col, Row, Grid } from "react-native-easy-grid";

import { LinearGradient } from 'expo-linear-gradient';

import Icon from '../Icon';
import { nowTheme } from "../../constants";

const { height, width } = Dimensions.get("screen");

class GradientHeader extends React.Component {

  render() {
    const { 
        navigation,
        shadowless, 
        success, 
        error, 
        primary,
        datauser,
        title, 
        back,
        gradientColor,
        borderRadius,
        profile,
        headerHeight,
        menu
    } = this.props;

    const inputStyles = [
      styles.input,
      !shadowless,
      success && styles.success,
      error && styles.error,
      primary && styles.primary,
      {...this.props.style}
    ];

    var left_width    = '0%';
    var center_width  = '0%';
    var right_width   = '0%';

    if(menu == 'Home'){
      left_width = '0%';
      center_width = '90%';
      right_width = '10%';
    } 

    else {
      left_width = '10%';
      center_width = '90%';
      right_width = '0%';
    }

    return (
      <Block>
        <LinearGradient
            // Button Linear Gradient
            colors={gradientColor}
            style={{
              borderBottomLeftRadius: borderRadius,
              borderBottomRightRadius: borderRadius,
            }}
            start={{ x: 0, y: 0 }}
            end={{ x: 1, y: 1 }}
          >
            <Block style={{paddingTop: 50, paddingBottom: headerHeight, paddingHorizontal: 20}}>

              <Block>
                <Block row>
                  {back == true ? (
                    <Block style={{width: left_width}}>
                      <Block center>
                        <TouchableWithoutFeedback 
                          onPress={() => navigation.goBack()}
                        >
                          <Icon
                              size={28}
                              color={nowTheme.COLORS.WHITE}
                              name={'arrow-left-circle'}
                              family={'Feather'}
                          />
                        </TouchableWithoutFeedback>
                      </Block>
                    </Block>
                  ) : <Block />}

                  <Block style={{width: center_width}}>
                    <Block style={{paddingHorizontal: 5}}>
                      <Text
                          style={{ fontFamily: 'montserrat-bold' }}
                          size={18}
                          color={nowTheme.COLORS.WHITE}
                      >
                          {title}
                      </Text>
                    </Block>
                  </Block>

                  {profile == true ? (
                    <Block style={{width: right_width}}>
                      <Block center>
                        <TouchableWithoutFeedback
                            onPress={() => navigation.navigate('Profile', {
                              id: datauser.id_user,
                            })}
                        >
                            <Icon
                              size={30}
                              color={nowTheme.COLORS.WHITE}
                              name={'user'}
                              family={'Feather'}
                            />
                        </TouchableWithoutFeedback>
                      </Block>
                    </Block>
                  ) : <Block />}
                </Block>

                {menu == 'Home' ? (
                  <Block>
                    <Block style={{top: height * 0.05}}>
                      <Block center>
                        <Text
                          style={{ 
                            fontFamily: 'montserrat-bold', 
                            textAlign: 'center' 
                          }}
                          size={20}
                          color={nowTheme.COLORS.WHITE}
                        >
                          {/* Welcome Back, {datauser.full_name} ! */}
                        </Text>
                      </Block>
                    </Block>
                  </Block>
                ) : <Block />}

              </Block>
            </Block>

        </LinearGradient>
      </Block>
    );
  }
}

GradientHeader.defaultProps = {
  shadowless: false,
  success: false,
  error: false,
  primary: false
};

GradientHeader.propTypes = {
  shadowless: PropTypes.bool,
  success: PropTypes.bool,
  error: PropTypes.bool,
  primary: PropTypes.bool
}

const styles = StyleSheet.create({
  input: {
    borderRadius: 30,
    borderColor: nowTheme.COLORS.BORDER,
    height: 44,
    backgroundColor: '#FFFFFF'
  },
  success: {
    borderColor: nowTheme.COLORS.INPUT_SUCCESS
  },
  error: {
    borderColor: nowTheme.COLORS.INPUT_ERROR
  },
  primary: {
    borderColor: nowTheme.COLORS.PRIMARY
  },
  shadow: {
    shadowColor: nowTheme.COLORS.BLACK,
    shadowOffset: { width: 0, height: 0.5 },
    shadowRadius: 1,
    shadowOpacity: 0.13,
    elevation: 2,
  }
});

export default withNavigation(GradientHeader);
