import React from "react";
import AwesomeAlert from 'react-native-awesome-alerts';
import { 
    ActivityIndicator,
    AsyncStorage,
    Dimensions,
    StyleSheet, 
    ScrollView 
} from "react-native";

import { 
    Block, 
    Button,
    Icon,
    Input, 
    theme, 
    Text 
} from "galio-framework";

import { ApprovalFUList } from "../../components";
import { nowTheme, approval_mrir, config } from "../../constants";
const { width } = Dimensions.get("screen");

class Approval extends React.Component {

    _isMounted = false;

    constructor() {
        super();

        this.state = {
          listData: [],
          isLoading: true,
          showAlert: false,
          successAlert: false,
          statusApproval: '',
          listApproval: [],
          remarks: '',
        };
    }

    async componentDidMount() {
        this._isMounted = true;

        var data_user = await AsyncStorage.getItem('data_user');
        var data_user = JSON.parse(data_user);

        var data_login = {
            project: data_user.project_id,
            api: data_user.api_key,
            id_user: data_user.id_user,
        }

        var id_master = this.props.route.params.id;

        this.setState({
            dataLogin: data_login,
            id_master: id_master
        });

        this.readData()

        return () => { isMounted = false };
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    checkCallback = (checked, id) =>{

        let approval = this.state.listApproval;

        if(checked == true){
            approval.push(id);
        } else {
            for(var i=0; i < approval.length; i++){
                if(approval[i] == id){
                    approval.splice(i, 1);
