            console.log(url);
            console.log(api);

            await fetch(url, {
                method: 'GET',
                headers: new Headers({
                    'Authorization': api
                }),
            })
            .then((response) => response.text())
            .then(data =>{

                var list_data = data;

                if(data.slice(-1) != '}'){
                    list_data = data + '}';
                }

                list_data = JSON.parse(list_data);

                this.setState({
                    listData: list_data.results_detail,
                    isLoading: false,
