    onSubmit = async() => {
        this.setState({
            showAlert: false
        });

        var data = [];

        this.state.listApproval.forEach(element => {
            data.push({
                'id': element,
                'action': this.state.statusApproval,
                'remarks': this.state.remarks,
            });
        });

        var id_master   = this.state.id_master;
        var id_user     = this.state.dataLogin.id_user;
        var api         = this.state.dataLogin.api;

        let options = {
            method: 'POST',
            headers: new Headers({
                'Authorization': api,
                'Content-Type': 'application/json'
            }),
            body: JSON.stringify({
                approved_by: id_user,
                approval: data,
                fit_id: id_master, 
            })
        };

        var url = config.config.url + '/api/fu/approval_process';

        await fetch(url, options)
        .then(response => response.text())
        .then(data => {

            console.log(data);

            this.setState({
                successAlert: true,
                listApproval: []
            });

            // this.props.navigation.navigate('Detail MV');

        })
        .catch(error => {
            console.error(error);
        });
    }

