import React from "react";
import AwesomeAlert from 'react-native-awesome-alerts';

import { 
    ActivityIndicator,
    AsyncStorage,
    FlatList,
    Dimensions,
    ImageBackground,
    StyleSheet, 
    ScrollView,
    TouchableWithoutFeedback,
    TouchableOpacity, 
} from "react-native";

import { 
    Block, 
    Input, 
    theme, 
    Button, 
} from "galio-framework";

import { 
    Autocomplete,
    WorkpackList, 
    DetailMVList,
    GradientHeader, 
    Filter, 
    ListChecked ,
    LoadMore ,
    Select,
    Icon,
    Text,
    Divider
} from "../../../components";

import {Camera} from 'expo-camera';
let camera = Camera;

import { nowTheme, detail_mrir, example_workpack, config } from "../../../constants";
const { height, width } = Dimensions.get("screen");

class MVSurveyorDetail extends React.Component {

    _isMounted = false;

    constructor() {
        super();
        // console.log();

        this.state = {
          listMaster: [],
          listDetail: [],
          listSubmit: [],
          listItems: [],

          showMaster: false,
          progress: 0,

          confirmAlert: false,
          successAlert: false,

          startCamera: false,
          typeCamera: Camera.Constants.Type.back,
          capturedImage: false,
          photoName: '',

          isLoading: false,
          isLoadData: false,
          loadButton: false,
          dataLogin: [],
          filter: '',
          category_template: '',
          menu: '',
          filterBy: ['Workpack_No', 'Template'],
          id: 0,
          id_master: 0,
          piecemark: [],

          workpack_no: '',
          data_workpack: {},
          Unique_No: {},
          photo: {},
        };
    }

    async componentDidMount() {
        this._isMounted = true;

        var data_user = await AsyncStorage.getItem('data_user');
        var data_user = JSON.parse(data_user);

        var data_login = {
            project: data_user.project_id,
            api: data_user.api_key,
            id_user: data_user.id_user,
        }

        this.setState({
            dataLogin: data_login,
            listSubmit: this.props.route.params.list_submit,
            data_workpack: this.props.route.params.data_workpack,
            id: this.props.route.params.id,
            id_master: this.props.route.params.id_master,
            menu: this.props.route.params.menu,
            workpack_no: this.props.route.params.workpack_no,
            piecemark: this.props.route.params.piecemark,
        });

        // console.log(this.state.listSubmit);

        return () => { isMounted = false };
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    render() {

        if(this.state.startCamera){
            return this.renderCamera()
        } else {
            return (
                <Block flex center style={styles.home}>

                    {this.state.isLoading ? (
                        <Block style={{ flex: 1, justifyContent: 'center', alignItems: 'center'  }}>
                            <ActivityIndicator size="large" color={nowTheme.COLORS.PRIMARY} />
                        </Block>
                    ) : (
                        <Block flex center style={styles.home}>
                            { this.renderHeader() }
                            { this.renderArticles() }
                            { this.renderAlert() }
                        </Block>
                    )}
                </Block>
            );
        }

    }

    renderHeader = () => {
        return(
            <GradientHeader 
              datauser={''}
              title={'Surveyor'}
              menu={''}
              back={true}
              gradientColor={nowTheme.COLORS.GRADIENT}
              borderRadius={5}
              profile={false}
              headerHeight={10}
            />
        )
    }

    renderArticles = () => {

        var piecemark_list = this.state.piecemark.join('\n');

        return (
            <Block flex>
                <ScrollView
                    showsVerticalScrollIndicator={false}
                    keyboardShouldPersistTaps='handled'
                    contentContainerStyle={''}
                >
                    <Block>
                        <Block style={{paddingTop: 10}}>
                            <Block card style={{width: width / 1.1, backgroundColor: nowTheme.COLORS.WHITE, paddingHorizontal: 20, paddingVertical: 10}}>

                                <Block style={{paddingVertical: 10}}>
                                    <Block center>
                                        <Text
                                            title={'DATA DETAIL'}
                                            size={15}
                                            bold
                                            color={nowTheme.COLORS.GRAY}
                                        />
                                    </Block>
                                </Block>

                                <Block style={{paddingVertical: 5, paddingBottom: 15}}>
                                    <Divider />
                                </Block>

                                <Block style={{paddingBottom: 10}}>
                                    <Block row>
                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={'Workpack No'}
                                                size={15}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block style={{width: '50%'}}>
                                            <Block>
                                                <Text
                                                    title={this.state.data_workpack.workpack_no}
                                                    size={15}
                                                    bold
                                                    color={nowTheme.COLORS.GRAY}
                                                />
                                            </Block>
                                        </Block>
                                    </Block>
                                </Block>

                                <Block style={{paddingBottom: 10}}>
                                    <Block row>
                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={'Piecemark List'}
                                                size={15}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block style={{width: '50%'}}>
                                            <Block>
                                                <Text
                                                    title={piecemark_list}
                                                    size={15}
                                                    bold
                                                    color={nowTheme.COLORS.GRAY}
                                                />
                                            </Block>
                                        </Block>
                                    </Block>
                                </Block>

                                <Block style={{paddingBottom: 10}}>
                                    <Block>
                                        <Block style={{paddingBottom: 5}}>
                                            <Text
                                                title={'Progress'}
                                                size={15}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block>
                                            <Select 
                                                selected={this.state.progress}
                                                value={['0', '25', '50', '75', '100']}
                                                symbol={'%'}
                                                selectChanged={this.selectChanged}
                                            />
                                        </Block>
                                    </Block>
                                </Block>

                                {
                                    this.state.progress == '100' ? (
                                        <Block>
                                            <Block style={{paddingBottom: 10}}>
                                                <Block>
                                                    <Text
                                                        title={'Unique No'}
                                                        size={15}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>

                                                <Block>
                                                    <Block>
                                                        <Autocomplete
                                                            selectedItem={this.selectedItem}
                                                            textChanged={this.textChanged}
                                                            title={'Unique No'}
                                                            value={Object.keys(this.state.Unique_No).length === 0 ? '' : this.state.Unique_No.name}
                                                            category={'Unique_No'}
                                                            categorySelected={this.state.categorySelected}
                                                            data={this.state.listItems}
                                                            menu={this.state.menu}
                                                            workpack={''}
                                                        />
                                                    </Block>
                                                </Block>
                                            </Block>

                                            <Block style={{paddingBottom: 10}}>
                                                <Block style={{paddingBottom: 5}}>
                                                    <Text
                                                        title={'Photo'}
                                                        size={15}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>

                                                <Block>
                                                    <Block>
                                                        <TouchableWithoutFeedback 
                                                            onPress={() => this.permissionCamera()}
                                                        >
                                                            <Block style={{backgroundColor: nowTheme.COLORS.PRIMARY, borderRadius: 5, paddingVertical: 8}}>
                                                                <Block center>
                                                                    <Icon
                                                                        size={25}
                                                                        color={nowTheme.COLORS.WHITE}
                                                                        name="camera"
                                                                        family="Feather"
                                                                    />
                                                                </Block>
                                                            </Block>
                                                        </TouchableWithoutFeedback>
                                                    </Block>
                                                </Block>
                                            </Block>
                                        </Block>
                                    ) : <Block />
                                }

                                <Block center>
                                    <Button 
                                        style={{width: width / 1.23}}
                                        onPress={() => this.setState({confirmAlert: !this.state.confirmAlert})}
                                        disabled={(this.state.progress == 100 ? !this.state.Unique_No : null )}
