    alertSuccess = () => {
        return(
            <Block>
                <Block 
                    style={{ 
                        textAlign: 'center',  
                    }}
                >
                    <Icon
                        size={50}
                        color={nowTheme.COLORS.PRIMARY}
                        name="check-circle"
                        family="Feather"
                        style={{
                            textAlign: 'center',
                        }}
                    />
                </Block>

                <Block>
                    <Text
                        title={'Data Submission Successful'}
                        size={20}
                        bold
                        color={nowTheme.COLORS.GRAY}
                        style={{
                            textAlign: 'center'
                        }}
                    />
                </Block>

                <Block center >
                    <Button 
