import React from "react";
import AwesomeAlert from 'react-native-awesome-alerts';

import { 
    ActivityIndicator,
    AsyncStorage,
    FlatList,
    Dimensions,
    ImageBackground,
    StyleSheet, 
    ScrollView,
    TouchableWithoutFeedback,
    TouchableOpacity, 
} from "react-native";

import { 
    Block, 
    Input, 
    theme, 
    Button, 
} from "galio-framework";

import { 
    Autocomplete,
    WorkpackList, 
    DetailMVList, 
    Filter, 
    ListChecked ,
    LoadMore ,
    Select,
    Icon,
    Text,
} from "../../components";

import {Camera} from 'expo-camera';
let camera = Camera;

import { nowTheme, detail_mrir, example_workpack, config } from "../../constants";
const { height, width } = Dimensions.get("screen");

class WorkpackPiecemarkSubmission extends React.Component {

    _isMounted = false;

    constructor() {
        super();
        // console.log();

        this.state = {
          listMaster: [],
          listDetail: [],
          listSubmit: [],
          listItems: [],

          showMaster: false,
          progress: 0,

          confirmAlert: false,
          successAlert: false,

          startCamera: false,
          typeCamera: Camera.Constants.Type.back,
          capturedImage: false,
          photoName: '',

          isLoading: false,
          isLoadData: false,
          loadButton: false,
          dataLogin: [],
          filter: '',
          category_template: '',
          menu: '',
          filterBy: ['Workpack_No', 'Template'],
          id: 0,
          id_master: 0,
          piecemark: [],

          workpack_no: '',
          data_workpack: {},
          Unique_No: {},
          photo: {},
        };
    }

    async componentDidMount() {
        this._isMounted = true;

        var data_user = await AsyncStorage.getItem('data_user');
        var data_user = JSON.parse(data_user);

        var data_login = {
            project: data_user.project_id,
            api: data_user.api_key,
            id_user: data_user.id_user,
        }

        this.setState({
            dataLogin: data_login,
            listSubmit: this.props.route.params.list_submit,
            data_workpack: this.props.route.params.data_workpack,
            id: this.props.route.params.id,
            id_master: this.props.route.params.id_master,
            menu: this.props.route.params.menu,
            workpack_no: this.props.route.params.workpack_no,
            piecemark: this.props.route.params.piecemark,
        });
        // console.log(this.state.photo);

        // console.log(this.state.dataLogin);

        return () => { isMounted = false };
    }

    readData = () => {

        this.setState({isLoading: true});

        var project = this.state.dataLogin.project;
        var api     = this.state.dataLogin.api;
        var id      = this.state.id;
        var menu    = this.state.menu;

        var filter = id + 'xx';

        var url = config.config.url + '/api/wp/list/' + menu + '/' + project + '/0/' + filter;

        console.log(url);
        console.log(api);

        fetch(url, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json',
                "Accept": "application/json",
                'Authorization': api,
            }),
        })
        .then((response) => response.text())
        .then((data) =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
                list_data = data + '}';
            }

            // console.log(list_data);

            list_data = JSON.parse(list_data);

            this.setState({
                listMaster: list_data.results_master,
                listDetail: list_data.results_detail,
                isLoading: false,
            });

            console.log(this.state.listMaster);

        }).catch((error) => {
            console.error(error);
        });
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    render() {

        if(this.state.startCamera){
            return this.renderCamera()
        } else {
            return (
                <Block flex center style={styles.home}>
                    {this.state.isLoading ? (
                        <Block style={{ flex: 1, justifyContent: 'center', alignItems: 'center'  }}>
                            <ActivityIndicator size="large" color={nowTheme.COLORS.PRIMARY} />
                        </Block>
                    ) : (
                        <Block flex center style={styles.home}>
                            { this.renderArticles() }
                            { this.renderAlert() }
                        </Block>
                    )}
                </Block>
            );
        }

    }

    renderArticles = () => {

        var piecemark_list = this.state.piecemark.join('\n');

        return (
            <Block flex>
                <ScrollView
                    showsVerticalScrollIndicator={false}
                    keyboardShouldPersistTaps='handled'
                    contentContainerStyle={''}
                >
                    <Block>
                        <Block style={{paddingTop: 10}}>
                            <Block card style={{width: width / 1.1, backgroundColor: nowTheme.COLORS.WHITE, paddingHorizontal: 20, paddingVertical: 10}}>

                                <Block center style={{paddingBottom: 10}}>
                                    <Text
                                        title={'Workpack No : ' + this.state.workpack_no}
                                        size={15}
                                        bold
                                        color={nowTheme.COLORS.GRAY}
                                    />
                                </Block>

                                <Block style={{paddingBottom: 10}}>
                                    <Block row>
                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={'Piecemark List'}
                                                size={15}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block style={{width: '50%'}}>
                                            <Block>
                                                <Text
                                                    title={piecemark_list}
                                                    size={15}
                                                    bold
                                                    color={nowTheme.COLORS.GRAY}
                                                />
                                            </Block>
                                        </Block>
                                    </Block>
                                </Block>

                                <Block style={{paddingBottom: 10}}>
                                    <Block>
                                        <Block>
                                            <Text
                                                title={'Progress'}
                                                size={15}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block>
                                            <Select 
                                                selected={this.state.progress}
                                                value={['0', '25', '50', '75', '100']}
                                                symbol={'%'}
                                                selectChanged={this.selectChanged}
                                            />
                                        </Block>
                                    </Block>
                                </Block>

                                {
                                    this.state.progress == '100' ? (
                                        <Block>
                                            <Block style={{paddingBottom: 10}}>
                                                <Block>
                                                    <Text
                                                        title={'Unique No'}
                                                        size={15}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>

                                                <Block>
                                                    <Block>
                                                        <Autocomplete
                                                            selectedItem={this.selectedItem}
                                                            textChanged={this.textChanged}
                                                            title={'Unique No'}
                                                            value={Object.keys(this.state.Unique_No).length === 0 ? '' : this.state.Unique_No.name}
                                                            category={'Unique_No'}
                                                            categorySelected={this.state.categorySelected}
                                                            data={this.state.listItems}
                                                            menu={this.state.menu}
                                                            workpack={''}
                                                        />
                                                    </Block>
                                                </Block>
                                            </Block>

                                            <Block style={{paddingBottom: 10}}>
                                                <Block>
                                                    <Text
                                                        title={'Photo'}
                                                        size={15}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>

                                                <Block>
                                                    <Block>
                                                        <TouchableWithoutFeedback 
                                                            onPress={() => this.permissionCamera()}
                                                        >
                                                            <Block style={{backgroundColor: nowTheme.COLORS.PRIMARY, borderRadius: 5, paddingVertical: 8}}>
                                                                <Block center>
                                                                    <Icon
                                                                        size={25}
                                                                        color={nowTheme.COLORS.WHITE}
                                                                        name="camera"
                                                                        family="Feather"
                                                                    />
                                                                </Block>
                                                            </Block>
                                                        </TouchableWithoutFeedback>
                                                    </Block>
                                                </Block>
                                            </Block>
                                        </Block>
                                    ) : <Block />
                                }

                                <Block center>
                                    <Button 
                                        style={{width: width / 1.25}}
                                        onPress={() => this.setState({confirmAlert: !this.state.confirmAlert})}
                                    >
                                        <Text
                                            title={'Submit'}
                                            size={15}
                                            bold
                                            color={nowTheme.COLORS.WHITE}
                                        />
                                    </Button>
                                </Block>

                            </Block>
                        </Block>
                    </Block>
                </ScrollView>
            </Block>
        );
    };

    permissionCamera = () => {
        // var status = await Camera.requestPermissionsAsync();
        // if (status === 'granted') {
            // start the camera
            this.setState({startCamera: true});
        // }
    }

    renderCamera = () => {

        if(this.state.capturedImage){
          return(
            <Block style={{ backgroundColor: 'transparent', flex: 1, width: '100%', height: '100%' }}>
              <ImageBackground source={{uri: this.state.photoName && this.state.photoName.uri}} style={{ flex: 1 }}>
                  <Block style={{ flex: 1, flexDirection: 'column', padding: 15, justifyContent: 'flex-end'}}>
                      <Block row>
                          <Block style={{style: '50%'}}>
                              <Button style={{backgroundColor: nowTheme.COLORS.WHITE}} onPress={() => this.setState({capturedImage: false})}>
                                  <Text
                                      title={'Re-Take'}
                                      bold
                                      color={nowTheme.COLORS.GRAY}
                                  />
                              </Button>
                          </Block>

                          <Block style={{style: '50%'}}>
                              <Button style={{backgroundColor: nowTheme.COLORS.WHITE}} onPress={() => this.savePicture()}>
                                <Text
                                    title={'Submit'}
                                    bold
                                    color={nowTheme.COLORS.GRAY}
                                />
                              </Button>
                          </Block>
                      </Block>
                  </Block>
              </ImageBackground>
          </Block>
          )
        } else {
          return(
            <Camera style={{flex: 1, justifyContent: 'center'}} type={this.state.typeCamera} ref={ref => { this.camera = ref; }}>

              <Block style={{flex: 1, justifyContent: 'flex-end', marginBottom: 50}}>
                <Block center style={{width: width / 1.1}}>
                    <Block row>
                        <Block style={{width: '20%', justifyContent: 'center'}}>
                            <Block center>
                                <TouchableOpacity
                                    onPress={() => {
                                        this.props.navigation.goBack()
                                    }}
                                >
                                    <Icon
                                        size={40}
                                        color={nowTheme.COLORS.WHITE}
                                        name="arrow-left-circle"
                                        family="Feather"
                                    />
                                </TouchableOpacity>
                            </Block>
                        </Block>

                        <Block center style={{width: '60%'}}>
                            <TouchableWithoutFeedback onPress={() => this.takePicture()}>
                                <Block>
                                    <Block style={{
                                        backgroundColor: nowTheme.COLORS.WHITE,
