    onSubmit = async() => {
        var unique_no   = this.state.Unique_No.name;
        var progress    = this.state.progress;
        var menu        = this.state.menu;
        var id_master   = this.state.id_master;
        var id_user     = this.state.dataLogin.id_user;
        var api         = this.state.dataLogin.api;

        var photo       = this.state.photoName;

        var data = [];

        this.state.id.forEach(element => {
            data.push(element);
        });

        let options = {
            method: 'POST',
            headers: new Headers({
                'Authorization': api,
                'Content-Type': 'application/json'
            }),
            body: JSON.stringify({
                // approved_by: id_user,
                id_master: id_master,
                submission: data,
                progress: progress,
                menu: menu,
                data_user: this.state.dataLogin,
                unique_no: this.state.Unique_No
            })
        };

        var url = config.config.url + '/api/wp/surveyor_submit_process';

        console.log(url);
        console.log(api);
        console.log(options);

        await fetch(url, options)
        .then(response => response.text())
        .then(data => {

            console.log(data);

            this.setState({
                successAlert: true,
                listApproval: []
            });

            // this.props.navigation.navigate('Detail MV');

        })
        .catch(error => {
            console.error(error);
        });


    }

    selectChanged = (item) => {
        this.setState({
            progress: 0
        });

        this.setState({
            progress: item
        });

    }

    selectedItem = (item, category) => {
        // this.state[category] = item;
        this.setState({[category] : item});
    }

    textChanged = (val, props) => {
        console.log(props);

        var search = val;
        var category = props;
        var project = this.state.dataLogin.project;
        var workpack_no = this.state.workpack_no;
        var api = this.state.dataLogin.api;

        if(search){
          var url = '';

          if(category == 'Unique_No'){
            url = config.config.url + '/api/unique_no/' + project + '/' + workpack_no + '/' + search;
          }

          console.log(url);
          console.log(api);

          fetch(url, {
              method: 'GET',
              headers: new Headers({
                  'Authorization': api
              }),
          })
          .then((response) => response.text())
          .then(data =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
              list_data = data + '}';
            }

            list_data = JSON.parse(list_data);

            this.setState({
              listItems: list_data.results,
              categorySelected: category,
            })

          }).catch((error) => {
            console.error(error);
          });

        } else {
          this.setState({
            listItems: []
          });
        }

    }

    renderContent = (item, index) => {
        return(
            <ListChecked 
                itemChecked={this.itemChecked}
                item={item}
                key={index}
                status={item.selected}
            />
        )
    }

    submitClicked = () => {
        var list_data = this.state.listSubmit;

        this.props.navigation.navigate('Workpack Piecemark Submission', {
            id: list_data,
        })
    }

    itemChecked = (checked, item) => {
        var id = item.id;

        let submit = this.state.listSubmit;

        if(checked == true){
            submit.push(id);
        } else {
            for(var i=0; i < submit.length; i++){
                if(submit[i] == id){
                    submit.splice(i, 1);
                }
            }
        }

        this.setState({listSubmit: submit});

        console.log(this.state.listSubmit);

    }

    searchClicked = () => {
        var workpack_id = this.state.Workpack_No.id;
        var workpack_no = this.state.Workpack_No.name;
        var phase = this.state.Workpack_No.phase;

        if(phase == 'PF'){
            this.props.navigation.navigate('Workpack Piecemark', {
                title: workpack_no,
                id: workpack_id,
            })
        } else {
            this.props.navigation.navigate('Workpack Joint', {
                title: workpack_no,
                id: workpack_id,
            })
        }

    }
