import React from "react";
import AwesomeAlert from 'react-native-awesome-alerts';
import { 
    ActivityIndicator,
    AsyncStorage,
    ImageBackground,
    Image,
    Dimensions,
    Keyboard,
    StyleSheet, 
    ScrollView,
    TouchableWithoutFeedback,
    View,
    TouchableOpacity,
} from "react-native";

import { 
    Block, 
    Input, 
    Button,
    theme, 
    Text, 
    Icon
} from "galio-framework";

import FlashMessage, { showMessage, hideMessage } from "react-native-flash-message";

import Select2 from "react-native-select-two"

import { GradientHeader} from "../components";
import { nowTheme, config } from "../constants";

import Autocomplete from '../components/Autocomplete';
import {Camera} from 'expo-camera';

const { height, width } = Dimensions.get("screen");

var iconColor = '#6FBE6F';
var gradientColor = ['#0BAB64', '#83D475'];
var index_employee = 1;
var filter = ['department', 'project', 'case'];

const DismissKeyboard = ({ children }) => (
    <TouchableWithoutFeedback onPress={() => Keyboard.dismiss()}>{children}</TouchableWithoutFeedback>
);

class Add extends React.Component {

    _isMounted = false;

    constructor() {
    super();

    this.state = {

    arr_employee: [''],

    listDataDepartment: [],
    flashSuccess: true,
    cekPhoto: false,
    startCamera: false,
    typeCamera: Camera.Constants.Type.back,
    capturedImage: false,
    photoName: '',

    badge   : '',
    department  : '',
    project     : '',
    case    : '',
    showAlertConfirm: false,

    listMasterDepartment: [],
    listMasterProject: [], 

    listData: [],
    isLoading: false,
    isLoadData: false,
    loadButton: false,
    dataLogin: [],
    filter: '',

    };
    }

    async componentDidMount() {
    this._isMounted = true;

    var data_user = await AsyncStorage.getItem('data_user');
    var data_user = JSON.parse(data_user);
    var data_login = {
    project: data_user.project_id,
    api: data_user.api_key,
    id_user: data_user.id_user,
    }

    this.setState({dataLogin: data_login});

    this.readDataDept()

    return () => { isMounted = false };
    }

    componentWillUnmount() {
    this._isMounted = false;
    }

    loadCallback = (limit) =>{

    var project = this.state.dataLogin.project;
    var api     = this.state.dataLogin.api;

    var url = config.config.url + '/api/iym/master_data';

    console.log(url);
    console.log(api);

    fetch(url, {
    method: 'GET',
    headers: new Headers({
        'Authorization': api
    }),
    })
    .then((response) => response.text())
    .then(data =>{

    var list_data = data;
    console.log(list_data);
    if(list_data.slice(-1) != '}'){
        list_data = data + '}';
    }

    list_data = JSON.parse(list_data);

    var all_data = this.state.listData;

    list_data.results.forEach(element => {
        all_data.push(element);
    });

    this.setState({
        listData: all_data,
        isLoadData: false,
        loadButton: (list_data.results.length >= 10 ? true : false),
    });

    });
    }

    filterCallback = (filterdata) => {
    this.setState({
    isLoadData : true,
    filter: filterdata,
    });

    var project   = this.state.dataLogin.project;
    var api   = this.state.dataLogin.api;

    var url = config.config.url + '/api/vt/list/pending/' + project + '/0/' + filterdata;

    console.log(url);
    console.log(api);

    fetch(url, {
    method: 'GET',
    headers: new Headers({
        'Authorization': api
    }),
    })
    .then((response) => response.text())
    .then(data =>{

    var list_data = data;

    if(data.slice(-1) != '}'){
        list_data = data + '}';
    }

    list_data = JSON.parse(list_data);

    this.setState({
        listData: list_data.results,
        isLoadData: false,
        loadButton: (list_data.results.length >= 10 ? true : false),
    });

    });
    }

    readDataDept = () => {
    var url = config.config.url + '/api/iym/master_data';
    var api   = this.state.dataLogin.api;

    fetch(url, {
    method: 'GET',
    headers: new Headers({
        'Content-Type': 'application/json',
        "Accept": "application/json",
        'Authorization': api,
    }),
    })
    .then((response) => response.text())
    .then((data) =>{

        var master_data_dept = data;

        master_data_dept = JSON.parse(master_data_dept);
        var arr_dept = master_data_dept.data_dept 
        var arr_project = master_data_dept.data_project 
        this.setState({
            listMasterDepartment: arr_dept,
            listMasterProject: arr_project,
            isLoading: false,
        });

    console.log('================================================')
        console.log(arr_dept) 
        console.log('xxxxxxxxxxxxxx')
        console.log(this.state.listMasterProject)     
    console.log('================================================')

    }).catch((error) => {
    console.error(error);
    });
    }

    renderFlashMessage = () => {
        if(this.state.flashSuccess==true){
            return (
                  <FlashMessage position="top" />
            );
        }
    }

    renderAutocomplete = (item, index, text_title) => {
    return (
    <Autocomplete
        onItemSelect={(itemSelect) => {
        this.setData(itemSelect, item)
        }}
        textInputValue={''}
        itemStyle={{
        padding: 10,
        marginTop: 2,
        backgroundColor: '#ddd',
        borderColor: '#bbb',
        borderWidth: 1,
        borderRadius: 5,
        fontFamily: 'montserrat-regular',
        }}
        itemTextStyle={{ 
        color: '#222', 
        fontFamily: 'montserrat-regular',
        }}
        category={item}
        menu={''}
        title={text_title}
        titleColor={iconColor}
        itemsContainerStyle={{ maxHeight: 140 }}
        resetValue={false}
        textInputProps={
        {
        placeholder: "Search " + text_title,
        underlineColorAndroid: "transparent",
        style: {
            paddingVertical: 5,
            paddingHorizontal: 10,
            borderWidth: 1,
            borderColor: '#ccc',
            borderRadius: 5,
            fontFamily: 'montserrat-regular',
        },
        }
        }
        listProps={
        {
        nestedScrollEnabled: true,
        }
        }
    />
    )
    }

    renderPicture = () => {
        if(this.state.capturedImage==true){
            console.log('here some pict.')
            return (
                <Block>
                    <Image 
                    style={styles.tinyLogo}
                    source={{uri:this.state.photoName.uri}}
                    />
                </Block>
            )
        }
    }

    add_employee = () => {
        let temp = index_employee++
        this.state.arr_employee.push(temp)
        this.setState({
            arr_employee: this.state.arr_employee
        })
