import React from 'react';
import { withNavigation } from '@react-navigation/compat';
import PropTypes from 'prop-types';

import { Col, Row, Grid } from "react-native-easy-grid";

import { 
    AsyncStorage,
    Dimensions,
    StyleSheet, 
    Image, 
    TouchableWithoutFeedback 
} from 'react-native';

import { 
  Block, 
  Checkbox, 
  theme, 
  Icon 
} from 'galio-framework';

import ContentApproval from '../ContentApproval';

import { nowTheme } from '../../constants';

import Text from '../Text';

const { height, width } = Dimensions.get("screen");

class VTInspectionComponentList extends React.Component {

  constructor() {
    super();

    this.state = {
      isChecked: false,
      isShow: false,
    };
  }

  checkedChange = (id) => {
    this.setState({isChecked: !this.state.isChecked});

    var checked = !this.state.isChecked;

    this.props.checkedChange(checked, id);

  }

  render() {
    const {
      navigation,
      item,
      horizontal,
      full,
      style,
      ctaColor,
      imageStyle,
      ctaRight,
      titleStyle,
      index
    } = this.props;

    const imageStyles = [full ? styles.fullImage : styles.horizontalImage, imageStyle];
    const titleStyles = [styles.cardTitle, titleStyle];
    const cardContainer = [styles.card, styles.shadow, style];
    const cardContainerActive = [styles.card, styles.cardActive, styles.shadow, style];
    const imgContainer = [
      styles.imageContainer,
      horizontal ? styles.horizontalStyles : styles.verticalStyles,
      styles.shadow
    ];

    return (
      <Block key={index}>
        <Block center>
          <Block style={{width: width / 1.1}}>
            <Block card flex style={this.state.isChecked == true ? cardContainerActive : cardContainer }>
              <TouchableWithoutFeedback
                onPress={() => this.checkedChange(item.id)}
              >
                <Block>
                  <Block style={{paddingHorizontal: 20, paddingTop: 10}}>

                    <Block center style={{paddingBottom: 10}}>
                      <Text
                        title={'Weld Map Drawing No : ' + item.weld_map}
                        bold
                        color={nowTheme.COLORS.GRAY}
                      />
                    </Block>

                    <Block row style={{paddingBottom: 10}}>
                      <Block style={{width: '50%'}}>
                        <Text
                          title={'Joint No'}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>

                      <Block style={{width: '50%'}}>
                        <Text
                          title={item.joint}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>
                    </Block>

                    <Block row style={{paddingBottom: 10}}>
                      <Block style={{width: '50%'}}>
                        <Text
                          title={'PWHT'}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>

                      <Block style={{width: '50%'}}>
                        <Text
                          title={item.pwht}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>
                    </Block>

                    <Block row style={{paddingBottom: 10}}>
                      <Block style={{width: '50%'}}>
                        <Text
                          title={'Weld Date Time'}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>

                      <Block style={{width: '50%'}}>
                        <Text
                          title={item.weld_datetime}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>
                    </Block>

                    <Block row style={{paddingBottom: 10}}>
                      <Block style={{width: '50%'}}>
                        <Text
                          title={'Consumable / Lot No'}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>

                      <Block style={{width: '50%'}}>
                        <Text
                          title={item.cons_lot_no}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>
                    </Block>

                    <Block center style={{paddingBottom: 10}}>
                      <TouchableWithoutFeedback onPress={() => this.setState({isShow: !this.state.isShow})}>
                        <Icon
                          size={25}
                          color={nowTheme.COLORS.GRAY}
                          name={ this.state.isShow ? "arrow-up-circle" : "arrow-down-circle"}
                          family="Feather"
                        />
                      </TouchableWithoutFeedback>
                    </Block>

                    {
                      this.state.isShow ? (
                        <Block>
                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Length of Weld (mm)'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.length_of_weld}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Length (mm)'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.length}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Location'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.location}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Thk'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.thk}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Dia (mm)'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.diameter}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Remarks'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.remarks}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'NDT Requirement'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.ndt_requirement}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Process RH'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.process_rh}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Process FC'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.process_fc}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Welder Ref RH'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.welder_ref_rh}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Welder Ref FC'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.welder_ref_fc}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'WPS No RH'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.wps_no_rh}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'WPS No FC'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.wps_no_fc}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                        </Block>
                      ) : <Block />
                    }

                  </Block>
                </Block>
              </TouchableWithoutFeedback>
            </Block>
          </Block>
        </Block>
      </Block>
    );
  }
}

const styles = StyleSheet.create({
  card: {
    backgroundColor: theme.COLORS.WHITE,
    marginVertical: theme.SIZES.BASE,
    minHeight: 80,
    marginBottom: 4
  },
  cardActive: {
    borderWidth: 2,
    borderColor: nowTheme.COLORS.PRIMARY,
  },
  cardTitle: {
    paddingHorizontal: 9,
    paddingTop: 7,
    paddingBottom: 15,
    textAlign: 'center',
  },
  cardDescription: {
    padding: theme.SIZES.BASE / 1
  },
  imageContainer: {
    borderRadius: 3,
    elevation: 1,
    overflow: 'hidden'
  },
  image: {
    // borderRadius: 3,
  },
  horizontalImage: {
    height: 122,
    width: 'auto'
  },
  horizontalStyles: {
    borderTopRightRadius: 0,
    borderBottomRightRadius: 0
  },
  verticalStyles: {
    borderBottomRightRadius: 0,
    borderBottomLeftRadius: 0
  },
  fullImage: {
    height: 215
  },
  shadow: {
    shadowColor: '#8898AA',
    shadowOffset: { width: 0, height: 1 },
    shadowRadius: 6,
    shadowOpacity: 0.1,
    elevation: 2
  },
  articleButton: {
    fontFamily: 'montserrat-bold',
    paddingHorizontal: 9,
    paddingVertical: 7
  },
  subtitlePadding: {
    paddingBottom: 5
  }
});

export default withNavigation(VTInspectionComponentList);
