import React from "react";

import { 
    ActivityIndicator,
    AsyncStorage,
    FlatList,
    Dimensions,
    StyleSheet, 
    ScrollView,
    TouchableWithoutFeedback 
} from "react-native";

import { 
    Block, 
    Input, 
    theme, 
} from "galio-framework";

import { 
    Autocomplete,
    WorkpackList, 
    DetailMVList, 
    Button, 
    Filter, 
    ListChecked ,
    LoadMore ,
    Icon,
    Text,
    Divider,
} from "../../../components";

import { nowTheme, detail_mrir, example_workpack, config } from "../../../constants";
const { height, width } = Dimensions.get("screen");

// ============================= EXAMPLE DATA ==============================
import workpack_master from "../../../constants/VT/example_data/surveyor/surveyor_master";
import workpack_detail from "../../../constants/VT/example_data/surveyor/surveyor_detail";
// =========================================================================

class VTSurveyor extends React.Component {

    _isMounted = false;

    constructor() {
        super();
        // console.log();

        this.state = {
          listMaster: [],
          listDetail: [],
          listSubmit: [],

          showMaster: false,

          isLoading: true,
          isLoadData: true,
          loadButton: false,
          dataLogin: [],
          filter: '',
          category_template: '',
          menu: '',
          filterBy: ['Workpack_No', 'Template'],
          id: 0,

          Workpack_No: {},
          data_workpack: {},
        };
    }

    async componentDidMount() {
        this._isMounted = true;

        var data_user = await AsyncStorage.getItem('data_user');
        var data_user = JSON.parse(data_user);

        var data_login = {
            project: data_user.project_id,
            api: data_user.api_key,
        }

        this.setState({
            dataLogin: data_login,
            id: this.props.route.params.id,
            menu: this.props.route.params.menu,
            data_workpack: this.props.route.params.data_workpack,
        });

        // console.log(this.props);
        this.readData();

        return () => { isMounted = false };
    }

    readData = () => {

        var project = this.state.dataLogin.project;
        var api     = this.state.dataLogin.api;
        var id      = this.state.data_workpack.id;
        var menu    = this.state.menu;

        var filter = id + 'xx';

        var url = config.config.url + '/api/wp/list/' + menu + '/' + project + '/0/' + filter;

        console.log(url);
        console.log(api);

        fetch(url, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json',
                "Accept": "application/json",
                'Authorization': api,
            }),
        })
        .then((response) => response.text())
        .then((data) =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
                list_data = data + '}';
            }

            // console.log(list_data);

            list_data = JSON.parse(list_data);

            this.setState({
                listMaster: list_data.results_master,
                listDetail: list_data.results_detail,
                isLoading: false,
                loadButton: (list_data.results_detail.length >= 10) ? true : false,
            });

            console.log(this.state.listDetail);

        }).catch((error) => {
            console.error(error);
        });
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    render() {
        return (
            <Block flex center style={styles.home}>
                {this.state.isLoading ? (
                    <Block style={{ flex: 1, justifyContent: 'center', alignItems: 'center'  }}>
                        <ActivityIndicator size="large" color={nowTheme.COLORS.PRIMARY} />
                    </Block>
                ) : (
                    <Block flex center style={styles.home}>
                        { this.renderFilter(this.state.listMaster) }
                        {/* { this.renderFilter(workpack_master) } */}
                        { this.renderArticles() }
                    </Block>
                )}
            </Block>
        );
    }

    renderFilter = (item) => {
        // var item = item[0];
        return(
            <Block center>
                <Block>
                    <Block style={{paddingVertical: 10}}>
                        <Block 
                            card  
                            style={{
                                width: width / 1.1, 
                                paddingVertical: 10, 
                                paddingHorizontal: 10, 
                                backgroundColor: nowTheme.COLORS.WHITE
                            }}
                        >

                            <Block>
                                <Block>
                                    <Block style={{paddingHorizontal: 10}}>
                                        <Block center>
                                            <Text
                                                title={'Workpack Detail'}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>
                                    </Block>
                                </Block>
                            </Block>

                            <Block style={{paddingVertical: 15, paddingHorizontal: 10}}>
                                <Divider />
                            </Block>

                            <Block row style={{paddingBottom: 5}}>
                                <Block style={{width: '50%'}}>
                                    <Block style={{paddingHorizontal: 10}}>
                                        <Block>
                                            <Text
                                                title={'Workpack No'}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>
                                    </Block>
                                </Block>

                                <Block style={{width: '50%'}}>
                                    <Block style={{paddingHorizontal: 10}}>
                                        <Text
                                            title={item.workpack_no}
                                            bold
                                            color={nowTheme.COLORS.GRAY}
                                        />
                                    </Block>
                                </Block>
                            </Block>

                            <Block >
                                <Block>
                                    <Block style={{paddingHorizontal: 10, paddingBottom: 5}}>
                                        <Block center>
                                            <TouchableWithoutFeedback onPress={() => this.setState({showMaster: !this.state.showMaster})}>
                                                <Icon
                                                    size={25}
                                                    color={nowTheme.COLORS.GRAY}
                                                    name={ this.state.showMaster ? "arrow-up-circle" : "arrow-down-circle"}
