import React from "react";

import { 
    ActivityIndicator,
    AsyncStorage,
    FlatList,
    Dimensions,
    StyleSheet, 
    ScrollView,
    TouchableWithoutFeedback 
} from "react-native";

import { 
    Block, 
    Button,
    Input, 
    theme, 
} from "galio-framework";

import { 
    Autocomplete,
    WorkpackList, 
    DetailMVList, 
    Divider,
    Filter, 
    ListChecked ,
    LoadMore ,
    Icon,
    Text,
} from "../../../components";

// ============================= EXAMPLE DATA ==============================
import inspection_master from "../../../constants/MV/example_data/inspection/inspection_master";
import inspection_detail from "../../../constants/MV/example_data/inspection/inspection_detail";
// =========================================================================

import { nowTheme, detail_mrir, example_workpack, config } from "../../../constants";
const { height, width } = Dimensions.get("screen");

class MVInspectionList extends React.Component {

    _isMounted = false;

    constructor() {
        super();
        // console.log();

        this.state = {
          listData: [],
          listSubmit: [],
          listPiecemark: [],

          showFilter: false,

          isLoading: true,
          isLoadData: false,
          loadButton: false,

          isSearch: false,

          dataLogin: [],
          filter: '',
          category_template: '',
          menu: '',
