    }

    loadCallback = (limit) =>{

        var project = this.state.dataLogin.project;
        var api     = this.state.dataLogin.api;

        var url = config.config.url + '/api/mv/list/pending/' + project + '/' + limit + '/' + this.state.filter;

        console.log(url);
        console.log(api);

        fetch(url, {
            method: 'GET',
            headers: new Headers({
                'Authorization': api
            }),
        })
        .then((response) => response.text())
        .then(data =>{

            var list_data = data;

            if(list_data.slice(-1) != '}'){
                list_data = data + '}';
            }

            list_data = JSON.parse(list_data);

            var all_data = this.state.listData;

            list_data.results.forEach(element => {
                all_data.push(element);
            });

            this.setState({
                listData: all_data,
                isLoadData: false,
                loadButton: (list_data.results.length >= 10 ? true : false),
            });

        });
    }

    submitClicked = () => {
        var id_master       = this.state.id;
        var list_data       = this.state.listSubmit;
        var list_piecemark  = this.state.listPiecemark;
        var workpack_no     = this.state.listMaster.workpack_no;
        var menu            = this.state.menu;

        this.props.navigation.navigate('MV Surveyor List Detail', {
            id: list_data,
            id_master: id_master,
            title: 'Piecemark Submission',
            piecemark: list_piecemark,
            workpack_no: workpack_no,
            menu: menu,
        })
    }

    itemChecked = (checked, item) => {
        var id          = item.id;
        var piecemark   = item.piecemark;

        let submit = this.state.listSubmit;
        let piecemark_list = this.state.listPiecemark;

        if(checked == true){
            submit.push(id);
            piecemark_list.push(piecemark);
        } else {
            for(var i=0; i < submit.length; i++){
                if(submit[i] == id){
                    submit.splice(i, 1);
                    piecemark_list.splice(i, 1);
                }
            }
        }

        this.setState({
            listSubmit: submit,
            listPiecemark: piecemark_list,
        });

        // console.log(this.state.listPiecemark);

    }

    selectedItem = (item, category) => {
        this.setState({
            [category] : item,
            listItems: [],
        });
    }

    textChanged = (val, props) => {
        var search = val;
        var category = props;
        var project = this.state.dataLogin.project;
        var api = this.state.dataLogin.api;

        if(search){
          var url = '';

          if(category == 'Discipline'){
            url = config.config.url + '/api/discipline/' + project + '/' + search;
          } else if(category == 'Module'){
            url = config.config.url + '/api/module/' + project + '/' + search;
          }

          console.log(url);
          console.log(api);

          fetch(url, {
              method: 'GET',
              headers: new Headers({
                  'Authorization': api
              }),
          })
          .then((response) => response.text())
          .then(data =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
              list_data = data + '}';
            }

            list_data = JSON.parse(list_data);

            this.setState({
              listItems: list_data.results,
              categorySelected: category,
            })

          }).catch((error) => {
            console.error(error);
          });

        } else {
          this.setState({
            listItems: []
          });
        }

    }

