import React from "react";
import AwesomeAlert from 'react-native-awesome-alerts';
import { 
    ActivityIndicator,
    AsyncStorage,
    FlatList,
    Dimensions,
    StyleSheet, 
    ScrollView,
    TouchableWithoutFeedback 
} from "react-native";

import { 
    Block, 
    Button,
    Input, 
    theme, 
} from "galio-framework";

import { 
    Autocomplete,
    WorkpackList, 
    DetailMVList, 
    Filter, 
    ListChecked ,
    LoadMore ,
    Icon,
    Text,
    VTInspectionComponentList
} from "../../../components";

// ============================= EXAMPLE DATA ==============================
import inspection_master from "../../../constants/MV/example_data/inspection/inspection_master";
import inspection_detail from "../../../constants/MV/example_data/inspection/inspection_detail";
// =========================================================================

import { nowTheme, detail_mrir, example_workpack, config } from "../../../constants";
const { height, width } = Dimensions.get("screen");

class VTInspectionList extends React.Component {

    _isMounted = false;

    constructor() {
        super();
        // console.log();

        this.state = {
          listMaster: [],
          listDetail: [],
          listApproval: [],
          listSubmit: [],
          listPiecemark: [],

          showFilter: false,
          showDetail: false,

          showAlertConfirm: false,
          statusApproval: '',
          alertSuccess: false,

          isLoading: true,
          isLoadData: false,
          loadButton: true,
          dataLogin: [],
          filter: '',
          category_template: '',
          menu: '',
          filterBy: ['Workpack_No', 'Template'],
          id: 0,

          Workpack_No: {},
          Module: {},
        };
    }

    async componentDidMount() {
        this._isMounted = true;

        var data_user = await AsyncStorage.getItem('data_user');
        var data_user = JSON.parse(data_user);

        var data_login = {
            project: data_user.project_id,
            api: data_user.api_key,
            id_user: data_user.id_user,
        }

        this.setState({
            dataLogin: data_login,
            id: this.props.route.params.id,
            menu: this.props.route.params.menu,
            listMaster: this.props.route.params.data_master,
        });

        // console.log(this.props);
        this.readData();

        return () => { isMounted = false };
    }

    readData = () => {

        var project         = this.state.dataLogin.project;
        var api             = this.state.dataLogin.api;
        var menu            = this.state.menu;
        var submission_id   = this.state.listMaster.submission_id;

        var url = config.config.url + '/api/vt/list/inspection_detail/'+ project + '/0/' + submission_id;

        console.log(url);
        console.log(api);

        fetch(url, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json',
                "Accept": "application/json",
                'Authorization': api,
            }),
        })
        .then((response) => response.text())
        .then((data) =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
                list_data = data + '}';
            }

            // console.log(list_data);

            list_data = JSON.parse(list_data);

            this.setState({
                listDetail: list_data.results,
                isLoading: false,
            });

        //     console.log(this.state.listMaster);

        }).catch((error) => {
            console.error(error);
        });
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    render() {
        return (
            <Block flex center style={styles.home}>
                {this.state.isLoading ? (
                    <Block style={{ flex: 1, justifyContent: 'center', alignItems: 'center'  }}>
                        <ActivityIndicator size="large" color={nowTheme.COLORS.PRIMARY} />
                    </Block>
                ) : (
                    <Block flex center style={styles.home}>
                        { this.renderFilter(this.state.listMaster) }
                        {/* { this.renderFilter(inspection_master) } */}
                        { this.renderArticles() }
                        { this.renderFooter() }
                        { this.renderAlert() }
                    </Block>
                )}
            </Block>
        );
    }

    renderFilter = (item) => {
        return(
            <Block center>
                <Block>
                    <Block style={{paddingVertical: 10}}>
                        <Block 
                            card  
                            style={{
                                width: width / 1.1, 
                                paddingVertical: 10, 
                                paddingHorizontal: 20, 
                                backgroundColor: nowTheme.COLORS.WHITE
                            }}
                        >

                            <Block>
                                <Block>
                                    <Block style={{paddingHorizontal: 20, paddingBottom: 10}}>
                                        <Block>
                                            <Block>
                                                <Block center>
                                                    <Text
                                                        title={'Visual'}
                                                        size={15}
                                                        bold
                                                        color={nowTheme.COLORS.GRAY}
                                                    />
                                                </Block>
                                            </Block>
                                        </Block>
                                    </Block>
                                </Block>
                            </Block>

                            <Block>

                                <Block style={{paddingBottom: 5}}>
                                    <Block row>
                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={'Drawing Number'}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={item.drawing_no}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>
                                    </Block>
                                </Block>

                                <Block style={{paddingBottom: 5}}>
                                    <Block row>
                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={'Workpack Number'}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={item.workpack_no}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>
                                    </Block>
