    onSubmit = async() => {
        this.setState({
            showAlertConfirm: false
        });

        var data = [];

        this.state.listApproval.forEach(element => {
            data.push({
                'id': element,
                'action': this.state.statusApproval,
                'remarks': "",
            });
        });

        var url = config.config.url + '/api/vt/approval_process';
        var api = this.state.dataLogin.api;

        let options = {
            method: 'POST',
            headers: new Headers({
                'Authorization': api,
                'Content-Type': 'application/json'
            }),
            body: JSON.stringify({
                data_user: this.state.dataLogin,
                approval: data,
                list_master: this.state.listMaster,
            })
        };


        console.log(url);
        console.log(api);
        console.log(options);

        await fetch(url, options)
        .then(response => response.text())
        .then(data => {

            console.log(data);

            this.setState({
                alertSuccess: true,
                listApproval: []
            });

            this.props.navigation.navigate('VT Category');

        })
        .catch(error => {
            console.error(error);
        });
    }

