import React from "react";

import { 
    ActivityIndicator,
    AsyncStorage,
    Dimensions,
    StyleSheet, 
    ScrollView,
    Picker,
    View, 
    TouchableOpacity,
} from "react-native";

import { 
    Block, 
    Input, 
    theme, 
    Text 
} from "galio-framework";

import {Camera} from 'expo-camera';

import { 
    Autocomplete, 
    WorkpackList, 
    ContentDetail, 
    GradientHeader, 
    DetailMVList, 
    Button, 
    Icon, 
    Filter,  
    FilterOne,  
    Select, 
    LoadMore 
} from "../../components";
import { nowTheme, detail_mrir, example_workpack_detail, config } from "../../constants";
const { height, width } = Dimensions.get("screen");

let camera = Camera;

var iconColor = '#6FBE6F';
var gradientColor = ['#0BAB64', '#83D475'];

class Detail extends React.Component {

    _isMounted = false;

    constructor(props) {
        super(props);
        // console.log();

        this.state = {
          listData: [],

            startCamera: false,
            typeCamera: Camera.Constants.Type.front,

            id: 0,

            isLoading: false,
            isChanged: false,
            isLoadData: false,
            loadButton: false,
            dataLogin: [],
            filter: '',
            filterBy: ['Workpack_No', 'Template'],
            category_template: '',
            progress: 0,
            progressComplete: false,
            Unique_No: {},
        };
    }

    async componentDidMount() {
        this._isMounted = true;

        var data_user = await AsyncStorage.getItem('data_user');
        var data_user = JSON.parse(data_user);

        var data_login = {
            project: data_user.project_id,
            api: data_user.api_key,
        }

        this.setState({
            dataLogin: data_login,
            category_template: this.props.route.params.category_template,
            id: this.props.route.params.id,
        });

        this.readData()

        return () => { isMounted = false };
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    startCamera = async () => {
        const {status} = await Camera.requestPermissionsAsync()
        if (status === 'granted') {
            // start the camera
            this.setState({startCamera: true});
        } else {
          Alert.alert('Access denied')
        }
    }

    loadCallback = (limit) =>{

        var project = this.state.dataLogin.project;
        var api     = this.state.dataLogin.api;

        var url = config.config.url + '/api/vt/list/pending/' + project + '/' + limit + '/' + this.state.filter;

        console.log(url);
        console.log(api);

        fetch(url, {
            method: 'GET',
            headers: new Headers({
                'Authorization': api
            }),
        })
        .then((response) => response.text())
        .then(data =>{

            var list_data = data;

            if(list_data.slice(-1) != '}'){
                list_data = data + '}';
            }

            list_data = JSON.parse(list_data);

            var all_data = this.state.listData;

            list_data.results.forEach(element => {
                all_data.push(element);
            });

            this.setState({
                listData: all_data,
                isLoadData: false,
                loadButton: (list_data.results.length >= 10 ? true : false),
            });

        });
    }

    filterCallback = (filterdata, category_template) => {
        this.setState({
            isLoadData : true,
            filter: filterdata,
            filterBy: category_template
        });

        var project   = this.state.dataLogin.project;
        var api       = this.state.dataLogin.api;

        var url = config.config.url + '/api/mv/list/submit/' + category_template[1] + '/' + project + '/0/' + filterdata;

        console.log(url);
        console.log(api);

        fetch(url, {
            method: 'GET',
            headers: new Headers({
                'Authorization': api
            }),
        })
        .then((response) => response.text())
        .then(data =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
                list_data = data + '}';
            }

            list_data = JSON.parse(list_data);

            this.setState({
                listData: list_data.results_detail,
                isLoadData: false,
                loadButton: (list_data.results_detail.length >= 10 ? true : false),
            });

        });
    }

    readData = () => {
        var project = this.state.dataLogin.project;
        var api     = this.state.dataLogin.api;
        var category_template = this.props.route.params.category_template;
        var id      = this.props.route.params.id;

        var url = config.config.url + '/api/wp/detail/' + category_template + '/' + id;

        console.log(url);
        console.log(api);

        fetch(url, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json',
                "Accept": "application/json",
                'Authorization': api,
            }),
        })
        .then((response) => response.text())
        .then((data) =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
                list_data = data + '}';
            }

            list_data = JSON.parse(list_data);

            this.setState({
                listData: list_data.results_detail,
                isLoading: false,
                // loadButton: (list_data.results_detail.length >= 10 ? true : false)
            });
        }).catch((error) => {
            console.error(error);
        });
    }

    renderMaster = () => {
        return(
            <Block>
                <Block style={{paddingVertical: 10, paddingHorizontal: 20}}>
                    <Block style={{ backgroundColor: 'red', paddingHorizontal: 20, paddingVertical: 10 }}>
                        <Block row>
                            <Block card style={{width: '90%'}}>
                                <Block style={{paddingHorizontal: 10}}>
                                    <Text>Workpack</Text>
                                </Block>
                            </Block>

                            <Block card style={{width: '10%'}}>
                                <Block right>
                                    <Icon
                                        size={20}
                                        color={nowTheme.COLORS.SECONDARY}
                                        name={ this.state.isShow == true ? "arrow-up-circle" : "arrow-down-circle"}
                                        family="Feather"
                                    />
                                </Block>
                            </Block>
                        </Block>
                    </Block>
                </Block>
            </Block>
        )
    }

    selectChanged = (item) => {
        this.setState({
            progress: 0
        });

        if(item == 100){
            var status = true;
        } else {
            var status = false;
        }


        this.setState({
            progressComplete: status,
            progress: item
        });

    }

    setData = (val, prop) => {

        const state = this.state;

        state['isChanged'] = true;
        this.setState(state);

        state[prop] = val;

        state[prop].index = 0;

        state['isChanged'] = false;
        this.setState(state);

    }

    saveClicked = () => {
        var id = this.state.id;
        var progress = this.state.progress;
        // var unique_no = this.state.Unique_No.name;


    }

    renderContent = (item, index) => {
        return(
            <Block key={index}>
                <Block center>
                    <Block card style={{width: width / 1.1, backgroundColor: nowTheme.COLORS.WHITE}}>
                        <Block style={{paddingHorizontal: 20, paddingVertical: 10}}>

                            <Block style={{paddingBottom: 10}}>
                                <Block card>
                                    <Block center>
                                        <Text
                                            style={{ 
                                                fontFamily: 'montserrat-bold',
                                            }}
                                            size={14}
                                            color={iconColor}
                                        >
                                            {this.state.category_template + ' : '}
                                        </Text>
                                    </Block>
                                </Block>
                            </Block>

                            <Block style={{paddingBottom: 10}}>
                                <ContentDetail
                                    title={'Drawing AS'}
                                    content={item.drawing_as}
                                />
                            </Block>

                            <Block style={{paddingBottom: 10}}>
                                <ContentDetail
                                    title={'Rev AS'}
                                    content={item.rev_as}
                                />
                            </Block>

                            <Block style={{paddingBottom: 10}}>
                                <ContentDetail
                                    title={'Material'}
                                    content={item.material}
                                />
                            </Block>

                            <Block style={{paddingBottom: 10}}>
                                <ContentDetail
                                    title={'Profile'}
                                    content={item.profile}
                                />
                            </Block>

                            <Block style={{paddingBottom: 10}}>
                                <ContentDetail
                                    title={'Grade'}
                                    content={item.grade}
                                />
                            </Block>

                            <Block style={{paddingBottom: 10}}>
                                <ContentDetail
                                    title={'Weight (kg)'}
                                    content={item.weight}
                                />
                            </Block>

                            <Block style={{paddingBottom: 10}}>
                                <Block>
                                    <Block row>
                                        <Block style={{width: '50%'}}>
                                            <Block style={{height: 30, justifyContent: 'center'}}>
                                                <Text
                                                    style={{ 
                                                        fontFamily: 'montserrat-bold',
                                                    }}
                                                    size={14}
                                                    color={iconColor}
                                                >
                                                    Progress
                                                </Text>
                                            </Block>
                                        </Block>

                                        <Block style={{width: '50%'}}>
                                            <Block>
                                                <Select 
                                                    selected={this.state.progress}
                                                    value={['0', '25', '50', '75', '100']}
                                                    symbol={'%'}
                                                    selectChanged={this.selectChanged}
                                                />
                                            </Block>
                                        </Block>
                                    </Block>
                                </Block>
                            </Block>

                            {this.state.progressComplete ? (
                                <Block>
                                    <Block style={{paddingBottom: 10}}>
                                        <Block>
                                            <Block row>
                                                <Block style={{width: '50%'}}>
                                                    <Block style={{height: 40, justifyContent: 'center'}}>
                                                        <Text
                                                            style={{ 
                                                                fontFamily: 'montserrat-bold',
                                                            }}
                                                            size={14}
                                                            color={iconColor}
                                                        >
                                                            Unique No
                                                        </Text>
                                                    </Block>
                                                </Block>

                                                <Block style={{width: '50%'}}>
                                                    <Block>
                                                        <FilterOne 
                                                            filterBy={['Unique_No']}
                                                        />
                                                    </Block>
                                                </Block>
                                            </Block>
                                        </Block>
                                    </Block>

                                    <Block style={{paddingBottom: 10}}>
                                        <Block>
                                            <Block row>
                                                <Block style={{width: '50%'}}>
                                                    <Block style={{height: 60, justifyContent: 'center'}}>
                                                        <Text
                                                            style={{ 
                                                                fontFamily: 'montserrat-bold',
                                                            }}
                                                            size={14}
                                                            color={iconColor}
                                                        >
                                                            Photo
                                                        </Text>
                                                    </Block>
                                                </Block>

                                                <Block style={{width: '50%'}}>
                                                    <Block>
                                                        <Block>
                                                            <Button style={{width: width / 3}} onPress={() => this.startCamera() }>
                                                                <Icon
                                                                    size={25}
                                                                    color={nowTheme.COLORS.WHITE}
                                                                    name="camera"
                                                                    family="Feather"
                                                                />
                                                            </Button>
                                                        </Block>
                                                    </Block>
                                                </Block>
                                            </Block>
                                        </Block>
                                    </Block>
                                </Block>
                            ) : <Block />}

                            <Block>
                                <Block>
                                    <Block center style={{paddingVertical: 20}}>
                                        <Button style={{width: width / 1.25}} onPress={() => this.saveClicked() }>
                                            <Text
                                                style={{ 
                                                    fontFamily: 'montserrat-bold',
                                                }}
                                                size={16}
                                                color={nowTheme.COLORS.WHITE}
                                            >
                                                Save
                                            </Text>
                                        </Button>
                                    </Block>
                                </Block>
                            </Block>

                        </Block>
                    </Block>
                </Block>
            </Block>
        )
    }

    renderArticles = () => {

        return (
            <Block>
                {/* { this.renderMaster() } */}

                <ScrollView
                    showsVerticalScrollIndicator={false}
                    contentContainerStyle={styles.articles}
                >
                    <Block flex>
                    {
                        this.state.listData.map((item, index) => {
                        // example_workpack_detail.map((item, index) => {
                            return(
                                this.renderContent(item, index)
                            );
                        })
                    }
                    </Block>

                </ScrollView>
        </Block>
        );
    };

    Loading = function(){
        return(
            <Block style={{ flex: 1, justifyContent: 'center', alignItems: 'center'  }}>
                <ActivityIndicator size="large" color={nowTheme.COLORS.PRIMARY} />
            </Block>
        );
    }

    renderHeader = () => {
        return(
            <GradientHeader 
                datauser={''}
                title={'Test'}
                menu={''}
                back={true}
                gradientColor={gradientColor}
                borderRadius={5}
                profile={false}
                headerHeight={10}
            />
        )
    }

    renderCamera = () => {
        return(
            <Camera style={{flex: 1}} type={this.state.typeCamera}>

                <Block style={{flex: 1, justifyContent: 'flex-end', marginBottom: 50}}>
                    <Block center style={{width: width / 1.1}}>
                        <Block row>
                            <Block style={{width: '20%', justifyContent: 'center'}}>
                                <Block center>
                                    <TouchableOpacity
                                        onPress={() => {
                                            if(this.state.typeCamera === Camera.Constants.Type.back){
                                                var status_camera = Camera.Constants.Type.front
                                            } else {
                                                var status_camera = Camera.Constants.Type.back
                                            }

                                            this.setState({startCamera: false});
                                        }}>            
                                            <Icon
                                                size={30}
                                                color={nowTheme.COLORS.WHITE}
                                                name="x-circle"
                                                family="Feather"
                                            />                
                                    </TouchableOpacity>
                                </Block>
                            </Block>

                            <Block center style={{width: '60%'}}>
                                <Block>
                                    <Block style={{
                                        backgroundColor: nowTheme.COLORS.WHITE,
                                        width: width / 6,
                                        height: height / 12.5,
                                        borderColor: nowTheme.COLORS.WHITE,
                                        borderWidth: 2,
                                        borderRadius: 50,
                                    }}>

                                    </Block>
                                </Block>
                            </Block>

                            <Block style={{width: '20%', justifyContent: 'center'}}>
                                <Block center>
                                    <TouchableOpacity
                                        onPress={() => {
                                            if(this.state.typeCamera === Camera.Constants.Type.back){
                                                var status_camera = Camera.Constants.Type.front
                                            } else {
                                                var status_camera = Camera.Constants.Type.back
                                            }

                                            this.setState({typeCamera: status_camera});
                                        }}>            
                                            <Icon
                                                size={30}
                                                color={nowTheme.COLORS.WHITE}
                                                name="refresh-ccw"
                                                family="Feather"
                                            />                
                                    </TouchableOpacity>
                                </Block>
                            </Block>
                        </Block>
                    </Block>

                </Block>
            </Camera>
        )
    }

    render() {

        return(
            (this.state.startCamera ? (this.renderCamera()) : (
                <Block flex center style={styles.home}>
                    {/* { this.renderHeader() } */}
                    { this.state.isLoading == true ? this.Loading() : this.renderArticles() }
                </Block>
            ) )
        );
    }
}

const styles = StyleSheet.create({
  home: {
    width: width
  },
  articles: {
    // width: width - theme.SIZES.BASE * 2,
    paddingVertical: theme.SIZES.BASE,
    // paddingHorizontal: 2,
    fontFamily: 'montserrat-regular'
  },

  container: {
    flex: 1,
  },
  camera: {
    flex: 1,
  },
  buttonContainer: {
    flex: 1,
    backgroundColor: 'transparent',
    flexDirection: 'row',
    margin: 20,
  },
  button: {
    flex: 0.1,
    alignSelf: 'flex-end',
    alignItems: 'center',
  },
  text: {
    fontSize: 18,
    color: 'white',
  },
});

export default Detail;
