    StyleSheet, 
    ScrollView ,
    TouchableWithoutFeedback,
} from "react-native";

import { 
    Block, 
    Button,
    // Icon,
    Input, 
    theme, 
    Text 
} from "galio-framework";

import { ApprovalMRIRList, Icon } from "../../components";
import { nowTheme, approval_mrir, config } from "../../constants";
import { isLoading } from "expo-font";

const DismissKeyboard = ({ children }) => (
    <TouchableWithoutFeedback onPress={() => Keyboard.dismiss()}>{children}</TouchableWithoutFeedback>
);

const { width } = Dimensions.get("screen");

class Approval extends React.Component {

    _isMounted = false;

    constructor() {
        super();

        this.state = {
          listData: [],
          isLoading: true,
          showAlert: false,
          successAlert: false,
          listApproval: [],
          id_master: 0,
          category: '',
          remarks: '',
        };
    }

    async componentDidMount() {
        this._isMounted = true;

        var data_user = await AsyncStorage.getItem('data_user');
        var data_user = JSON.parse(data_user);

        var data_login = {
            project: data_user.project_id,
            api: data_user.api_key,
            id_user: data_user.id_user,
        }

        var id_master   = this.props.route.params.id;
        var category    = this.props.route.params.category;

        this.setState({
            dataLogin: data_login,
            id_master: id_master,
            category: category,
        });

        this.readData()

        return () => { isMounted = false };
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    readData = async(limit) => {
        var id          = this.state.id_master;
        var api         = this.state.dataLogin.api;

        var url = config.config.url + '/api/mrir/detail/pending/' + this.state.category + '/' + id;

        console.log(url);
        console.log(api);

        await fetch(url, {
            method: 'GET',
            headers: new Headers({
                'Authorization': api
            }),
        })
        .then((response) => response.text())
        .then(data =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
                list_data = data + '}';
            }

            console.log(list_data);

            list_data = JSON.parse(list_data);

            this.setState({
                listData: list_data.results,
                isLoading: false,
            });

        });
    }

    checkCallback = (checked, id) =>{

        let approval = this.state.listApproval;

        if(checked == true){
            approval.push(id);
        } else {
            for(var i=0; i < approval.length; i++){
                if(approval[i] == id){
                    approval.splice(i, 1);
                }
            }
        }

        this.setState({listApproval: approval});

        console.log(this.state.listApproval);
    }

    renderArticles = () => {
        return (
            <Block>
                <ScrollView
                    showsVerticalScrollIndicator={false}
                    contentContainerStyle={styles.articles}
                >
                    <Block flex>
                    {
                        this.state.listData.map((item, index) => {
                        // approval_mrir.map((item, index) => {
                            return(
                                <Block flex row key={index}>
                                    <ApprovalMRIRList 
                                        item={item}
                                        checkboxChange={this.checkCallback}
                                        horizontal 
                                    />
                                </Block>
                            );
                        })
                    }
                    </Block>
                </ScrollView>

                <Block>
                    { this.approvalButton() }
                </Block>
            </Block>
        );
    };

    Loading = function(){
        return(
            <Block style={{ flex: 1, justifyContent: 'center', alignItems: 'center'  }}>
                <ActivityIndicator size="large" color={nowTheme.COLORS.PRIMARY} />
            </Block>
        );
    }

    approvalButton = () => {
        return(
            <Block>

                <Block>
                    <Text
                        style={{ fontFamily: 'montserrat-bold' }}
                        size={14}
                    >
                        Total Item: {this.state.listApproval.length}
                    </Text>
                </Block>

                <Block row space='between'>
                    <Block>
                        <Button 
                            label='' 
                            onPress={() => this.setState({showAlert: true, statusApproval: 'A'})}
                            disabled={this.state.listApproval.length == 0}
                            color={(this.state.listApproval.length == 0) ? theme.COLORS.BLOCK : nowTheme.COLORS.PRIMARY } 
                            style={{
                                width: width / 3.7,
                            }}
                        >
                            <Text
                                style={{ fontFamily: 'montserrat-bold' }}
                                size={14}
                                color={nowTheme.COLORS.WHITE}
                            >
                                Approve
                            </Text>
                        </Button>
                    </Block>

                    <Block>
                        <Button 
                            label='' 
                            onPress={() => this.setState({showAlert: true, statusApproval: 'R'})} 
                            color={(this.state.listApproval.length == 0) ? theme.COLORS.BLOCK : nowTheme.COLORS.ERROR }
                            style={{
                                width: width / 3.7,
                            }}
                        >
                            <Text
                                style={{ fontFamily: 'montserrat-bold' }}
                                size={14}
                                color={nowTheme.COLORS.WHITE}
                            >
                                Reject
                            </Text>
                        </Button>
                    </Block>

                    <Block>
                        <Button 
                            label='' 
                            onPress={() => this.setState({showAlert: true, statusApproval: 'P'})} 
                            color={(this.state.listApproval.length == 0) ? theme.COLORS.BLOCK : nowTheme.COLORS.FACEBOOK }
                            style={{
                                width: width / 3.7,
                            }}
                        >
                            <Text
                                style={{ 
                                    fontFamily: 'montserrat-bold',
                                    textAlign: 'center', 
                                }}
                                size={14}
                                color={nowTheme.COLORS.WHITE}
                            >
                                Pending By QC
                            </Text>
                        </Button>
                    </Block>
                </Block>

            </Block>

        );
    }

    alertApproval = () => {
        return(
            <Block>
                <AwesomeAlert
                    show={this.state.showAlert}
                    // show={true}
                    customView={this.alert()}
                    closeOnTouchOutside={false}
                    closeOnHardwareBackPress={false}
                />
            </Block>
        );
    }

    onSubmit = async() => {
        this.setState({
            showAlert: false
        });

        var data = [];

        this.state.listApproval.forEach(element => {
            data.push({
                'id': element,
                'action': this.state.statusApproval,
                'remarks': this.state.remarks,
            });
        });

        var id_master   = this.state.id_master;
        var id_user     = this.state.dataLogin.id_user;
        var api         = this.state.dataLogin.api;

        let options = {
            method: 'POST',
            headers: new Headers({
                'Authorization': api,
                'Content-Type': 'application/json'
            }),
            body: JSON.stringify({
                approved_by: id_user,
                approval: data,
            })
        };

        var url = config.config.url + '/api/mrir/approval_process';

        await fetch(url, options)
        .then(response => response.text())
        .then(data => {

            console.log(data);

            this.setState({
                successAlert: true,
                listApproval: []
            });

            // this.props.navigation.navigate('Detail MV');

        })
        .catch(error => {
            console.error(error);
        });
    }

    inputValueUpdate = (val, prop) => {
        const state = this.state;
        state[prop] = val;
        this.setState(state);
    }

    alertConfirm = () => {
        return(
            <Block>
                <Block 
                    style={{ 
                        textAlign: 'center',  
                    }}
                >
                    <Icon
                        size={50}
                        color={nowTheme.COLORS.ERROR}
                        name="warning"
                        family="AntDesign"
                        style={{
                            textAlign: 'center',
                        }}
                    />
                </Block>

                <Block>
                    <Text
                        size={18}
                        style={{ 
                            fontFamily: 'montserrat-regular',
                            textAlign: 'center', 
                            marginTop: 10,
                        }}
                    >
                        {this.textAlert(this.state.statusApproval)}

                        {' \n\n Total Item : ' + this.state.listApproval.length }

                    </Text>
                </Block>

                { this.state.statusApproval != 'A' ? (
                    <Block center style={{paddingVertical: 15}} >
                        <Input
                            placeholder="Remarks"
                            placeholderTextColor={nowTheme.COLORS.MUTED}
                            style={styles.input}
                            onChangeText={(val) => this.inputValueUpdate(val, 'remarks')}
                            color={nowTheme.COLORS.HEADER}
                        />
                    </Block>
                ) : <Block />}

                <Block row center >
                    <Button 
                        size={'small'}
                        onPress={ () => this.onSubmit() }
                    >
                        <Text
                            style={{ fontFamily: 'montserrat-bold' }}
                            size={14}
                            color={nowTheme.COLORS.WHITE}
                        >
                            YES
                        </Text>
                    </Button>

                    <Button 
                        size={'small'}
                        color={nowTheme.COLORS.ERROR}
                        onPress={ () => this.setState({showAlert: false}) }
                    >
                        <Text
                            style={{ fontFamily: 'montserrat-bold' }}
                            size={14}
                            color={nowTheme.COLORS.WHITE}
                        >
                            NO
                        </Text>
                    </Button>
                </Block>

            </Block>
        );
    }

    alertSuccess = () => {
        return(
            <Block>
                <Block 
                    style={{ 
                        textAlign: 'center',  
                    }}
                >
                    <Icon
                        size={50}
                        color={nowTheme.COLORS.PRIMARY}
                        name="check-circle"
                        family="Feather"
                        style={{
                            textAlign: 'center',
                        }}
                    />
                </Block>

                <Block>
                    <Text
                        size={20}
                        style={{ 
                            fontFamily: 'montserrat-regular',
                            textAlign: 'center', 
                            marginBottom: 5,
                            marginTop: 10,
                        }}
                    >
                        {' Data successful ' + (this.state.statusApproval == 'A' ? 'Approved' : 'Rejected') }

                    </Text>
                </Block>

                <Block center >
                    <Button 
                        onPress={ () => {
                            this.setState({successAlert: false});
                            this.props.navigation.navigate('Category MRIR');
                        }}
                        color={nowTheme.COLORS.PRIMARY}
                    >
                        <Text
                            style={{ fontFamily: 'montserrat-bold' }}
                            size={14}
                            color={nowTheme.COLORS.WHITE}
                        >
                            OK
                        </Text>
                    </Button>

                </Block>
            </Block>
        );
    }

    alertApproval = () => {
        return(
            <Block>
                <AwesomeAlert
                    show={this.state.showAlert}
                    // show={true}
                    customView={this.alert()}
                    closeOnTouchOutside={false}
                    closeOnHardwareBackPress={false}
                />
            </Block>
        );
    }

    textAlert = (status) => {
        var text = '';
        if(status == 'A'){
            text = 'Approve';
        } else if(status == 'R'){
            text = 'Reject';
        } else {
            text = 'Pending By QC';
        }

        return(
            'Are you sure want to ' + text + ' this data?'
        );
    }

    placeAlert = () => {
        return(
            <Block>
                <Block>
                    <AwesomeAlert
                        show={this.state.showAlert}
                        // show={true}
                        customView={this.alertConfirm()}
                        closeOnTouchOutside={false}
                        closeOnHardwareBackPress={false}
                    />
                </Block>

                <Block>
                    <AwesomeAlert
                        show={this.state.successAlert}
                        // show={true}
                        customView={this.alertSuccess()}
                        closeOnTouchOutside={false}
                        closeOnHardwareBackPress={false}
                    />
                </Block>
            </Block>
        );
    }

    render() {
        return (
            <DismissKeyboard>
                <Block flex center style={styles.home}>
                    { this.state.isLoading == true ? this.Loading() : this.renderArticles() }
                    { this.placeAlert() }
                </Block>
            </DismissKeyboard>
        );
    }
}

const styles = StyleSheet.create({
  home: {
    width: width
  },
  articles: {
    width: width - theme.SIZES.BASE * 2,
    paddingVertical: theme.SIZES.BASE,
    paddingHorizontal: 2,
    fontFamily: 'montserrat-regular'
  },
  input: {
    borderRadius: 5,
    borderColor: nowTheme.COLORS.BORDER,
    height: 44,
    backgroundColor: '#FFFFFF',
    fontFamily: 'montserrat-regular'
  },
});

export default Approval;
