import React from 'react';
import { withNavigation } from '@react-navigation/compat';
import PropTypes from 'prop-types';

import { Col, Row, Grid } from "react-native-easy-grid";

import { 
    AsyncStorage,
    Dimensions,
    StyleSheet, 
    Image, 
    TouchableWithoutFeedback 
} from 'react-native';

import { 
  Block, 
  Checkbox, 
  theme, 
  Icon 
} from 'galio-framework';

import ContentApproval from '../ContentApproval';

import { nowTheme } from '../../constants';

import Text from '../Text';
import Divider from '../Divider';

const { height, width } = Dimensions.get("screen");

class MVInspectionComponentList extends React.Component {

  constructor() {
    super();

    this.state = {
      isChecked: false,
      isShow: false,
    };
  }

  checkedChange = (id) => {
    this.setState({isChecked: !this.state.isChecked});

    var checked = !this.state.isChecked;

    this.props.checkedChange(checked, id);

  }

  render() {
    const {
      navigation,
      item,
      horizontal,
      full,
      style,
      ctaColor,
      imageStyle,
      ctaRight,
      titleStyle,
      index
    } = this.props;

    const imageStyles = [full ? styles.fullImage : styles.horizontalImage, imageStyle];
    const titleStyles = [styles.cardTitle, titleStyle];
    const cardContainer = [styles.card, styles.shadow, style];
    const cardContainerActive = [styles.card, styles.cardActive, styles.shadow, style];
    const imgContainer = [
      styles.imageContainer,
      horizontal ? styles.horizontalStyles : styles.verticalStyles,
      styles.shadow
    ];

    return (
      <Block key={index}>
        <Block center>
          <Block style={{width: width / 1.1}}>
            <Block card flex style={this.state.isChecked == true ? cardContainerActive : cardContainer }>
              <TouchableWithoutFeedback
                onPress={() => this.checkedChange(item.id)}
              >
                <Block>
                  <Block style={{paddingHorizontal: 20, paddingTop: 10}}>

                    <Block center style={{paddingBottom: 10}}>
                      <Text
                        title={'Piecemark No : ' + item.id_piecemark}
                        bold
                        color={nowTheme.COLORS.GRAY}
                      />
                    </Block>

                    <Block style={{paddingBottom: 15}}>
                      <Divider />
                    </Block>

                    <Block row style={{paddingBottom: 10}}>
                      <Block style={{width: '50%'}}>
                        <Text
                          title={'Can No'}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>

                      <Block style={{width: '50%'}}>
                        <Text
                          title={item.can_no}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>
                    </Block>

                    <Block row style={{paddingBottom: 10}}>
                      <Block style={{width: '50%'}}>
                        <Text
                          title={'Unique No'}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>

                      <Block style={{width: '50%'}}>
                        <Text
                          title={item.unique_no}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>
                    </Block>

                    <Block row style={{paddingBottom: 10}}>
                      <Block style={{width: '50%'}}>
                        <Text
                          title={'Spec / Grade'}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>

                      <Block style={{width: '50%'}}>
                        <Text
                          title={item.grade}
                          bold
                          color={nowTheme.COLORS.GRAY}
                        />
                      </Block>
                    </Block>

                    <Block center style={{paddingBottom: 10}}>
                      <TouchableWithoutFeedback onPress={() => this.setState({isShow: !this.state.isShow})}>
                        <Icon
                          size={25}
                          color={nowTheme.COLORS.GRAY}
                          name={ this.state.isShow ? "arrow-up-circle" : "arrow-down-circle"}
                          family="Feather"
                        />
                      </TouchableWithoutFeedback>
                    </Block>

                    {
                      this.state.isShow ? (
                        <Block>
                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Heat No'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.heat_no}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Thickness'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.thk}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Profile'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.profile}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'Material Description'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.material}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                          <Block row style={{paddingBottom: 10}}>
                            <Block style={{width: '50%'}}>
                              <Text
                                title={'MRIR No'}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>

                            <Block style={{width: '50%'}}>
                              <Text
                                title={item.mrir_no}
                                bold
                                color={nowTheme.COLORS.GRAY}
                              />
                            </Block>
                          </Block>

                        </Block>
                      ) : <Block />
                    }

                  </Block>
                </Block>
              </TouchableWithoutFeedback>
            </Block>
          </Block>
        </Block>
      </Block>
    );
  }
