import React from "react";
import AwesomeAlert from 'react-native-awesome-alerts';

import { 
    ActivityIndicator,
    AsyncStorage,
    FlatList,
    Dimensions,
    StyleSheet, 
    ScrollView,
    TouchableWithoutFeedback 
} from "react-native";

import { 
    Block, 
    Input, 
    theme, 
    Button, 
} from "galio-framework";

import { 
    Autocomplete,
    WorkpackList, 
    DetailMVList, 
    Filter, 
    ListChecked ,
    LoadMore ,
    Select,
    SelectMultiple,
    Icon,
    Text,
    Divider,
} from "../../../components";

import { 
    nowTheme, 
    detail_mrir, 
    example_workpack, 
    config,
} from "../../../constants";

import weld_process from '../../../constants/weld_process';

import {Camera} from 'expo-camera';
let camera = Camera;

import { element } from "prop-types";
const { height, width } = Dimensions.get("screen");

class VTSurveyorDetail extends React.Component {

    _isMounted = false;

    constructor() {
        super();
        // console.log();

        this.state = {
          listMaster: [],
          listDetail: [],
          listSubmit: [],
          listItems: [],

          showMaster: false,
          progress: 100,
          confirmAlert: false,
          categorySelected: '',

          isLoading: true,
          isLoadData: false,
          loadButton: false,
          dataLogin: [],
          filter: '',
          category_template: '',
          menu: '',
          filterBy: ['Workpack_No', 'Template'],
          id: 0,
          item: [],
          id_master: 0,

          workpack_no: '',
          Unique_No: {},
          Fitter_Code: [],
          Tack_Weld_ID: [],
          WPS: [],
          data_workpack: {},

          startCamera: false,
          typeCamera: Camera.Constants.Type.back,
          capturedImage: false,
          photoName: '',

          Process_RH: [],
          Process_FC: [],
          ID_RH: [],
          ID_FC: [],
          WPS_RH: [],
          WPS_FC: [],
          Location: {},
          cost_lot_no: '',
          remarks: '',
        };
    }

    async componentDidMount() {
        this._isMounted = true;

        var data_user = await AsyncStorage.getItem('data_user');
        var data_user = JSON.parse(data_user);

        var data_login = {
            project: data_user.project_id,
            api: data_user.api_key,
            id_user: data_user.id_user,
        }

        this.setState({
            dataLogin: data_login,
            item: this.props.route.params.item,
            menu: this.props.route.params.menu,
            data_workpack: this.props.route.params.data_workpack,
            progress: this.props.route.params.item.progress,
            isLoading: false,
            // workpack_no: this.props.route.params.workpack_no,
        });

        console.log(this.props.route.params.item.progress);

        return () => { isMounted = false };
    }

    readData = () => {

        this.setState({isLoading: true});

        var project = this.state.dataLogin.project;
        var api     = this.state.dataLogin.api;
        var id      = this.state.id;
        var menu    = this.state.menu;

        var filter = id + 'xx';

        var url = config.config.url + '/api/wp/list/' + menu + '/' + project + '/0/' + filter;

        console.log(url);
        console.log(api);

        fetch(url, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json',
                "Accept": "application/json",
                'Authorization': api,
            }),
        })
        .then((response) => response.text())
        .then((data) =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
                list_data = data + '}';
            }

            // console.log(list_data);

            list_data = JSON.parse(list_data);

            this.setState({
                listMaster: list_data.results_master,
                listDetail: list_data.results_detail,
                isLoading: false,
            });

            console.log(this.state.listMaster);

        }).catch((error) => {
            console.error(error);
        });
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    render() {
        if(this.state.startCamera){
            return this.renderCamera()
        } else {
            return (
                <Block flex center style={styles.home}>
                    {this.state.isLoading ? (
                        <Block style={{ flex: 1, justifyContent: 'center', alignItems: 'center'  }}>
                            <ActivityIndicator size="large" color={nowTheme.COLORS.PRIMARY} />
                        </Block>
                    ) : (
                        <Block flex center style={styles.home}>
                            { this.renderArticles() }
                            { this.renderAlert() }
                        </Block>
                    )}
                </Block>
            );
        }
    }

    inputValueUpdate = (val, prop) => {
        const state = this.state;
        state[prop] = val;
        this.setState(state);
    }

    renderArticles = () => {

        return (
            <Block flex>
                <ScrollView
                    showsVerticalScrollIndicator={false}
                    keyboardShouldPersistTaps='handled'
                    contentContainerStyle={''}
                >
                    <Block>
                        <Block style={{paddingTop: 10}}>
                            <Block card style={{width: width / 1.1, backgroundColor: nowTheme.COLORS.WHITE, paddingHorizontal: 20, paddingVertical: 10}}>

                                <Block style={{paddingTop: 10}}>
                                    <Text
                                        title={'DATA DETAIL'}
                                        bold
                                        color={nowTheme.COLORS.GRAY}
                                        center
                                    />
                                </Block>

                                <Block style={{paddingVertical: 15}}>
                                    <Divider />
                                </Block>

                                <Block style={{paddingBottom: 10}}>
                                    <Block row>
                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={'Drawing Weld Map'}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block style={{width: '50%'}}>
                                            <Block>
                                                <Text
                                                    title={this.state.item.drawing_wm}
                                                    bold
                                                    color={nowTheme.COLORS.GRAY}
                                                />
                                            </Block>
                                        </Block>
                                    </Block>
                                </Block>

                                <Block style={{paddingBottom: 10}}>
                                    <Block row>
                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={'Joint No'}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block style={{width: '50%'}}>
                                            <Block>
                                                <Text
                                                    title={this.state.item.joint_no}
                                                    bold
                                                    color={nowTheme.COLORS.GRAY}
                                                />
                                            </Block>
                                        </Block>
                                    </Block>
                                </Block>

                                <Block style={{paddingBottom: 10}}>
                                    <Block row>
                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={'Weld Type'}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block style={{width: '50%'}}>
                                            <Block>
                                                <Text
                                                    title={this.state.item.weld_type}
                                                    bold
                                                    color={nowTheme.COLORS.GRAY}
                                                />
                                            </Block>
                                        </Block>
                                    </Block>
                                </Block>

                                <Block style={{paddingBottom: 10}}>
                                    <Block row>
                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={'Dia (Inch)'}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={this.state.item.diameter}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>
                                    </Block>
                                </Block>

                                <Block style={{paddingBottom: 10}}>
                                    <Block row>
                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={'Thk (mm)'}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block style={{width: '50%'}}>
                                            <Text
                                                title={this.state.item.thk}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>
                                    </Block>
                                </Block>

                                <Block style={{paddingBottom: 10}}>
                                    <Block>
                                        <Block>
                                            <Text
                                                title={'Progress'}
                                                size={15}
                                                bold
                                                color={nowTheme.COLORS.GRAY}
                                            />
                                        </Block>

                                        <Block>
                                            <Select 
                                                selected={this.state.progress}
                                                value={['0', '25', '50', '75', '100']}
                                                symbol={'%'}
                                                selectChanged={this.selectChanged}
                                            />
                                        </Block>
                                    </Block>
                                </Block>

                                {
                                    this.state.progress == '100' ? (
                                        <Block>

                                            <Block style={{paddingBottom: 10}}>
                                                <Block>
                                                    <Block>
                                                        <Text
                                                            title={'Cost / Lot No.'}
                                                            size={15}
                                                            bold
                                                            color={nowTheme.COLORS.GRAY}
                                                        />
                                                    </Block>

                                                    <Block>
                                                        <Input 
                                                            placeholder="Cost / Lot No"
                                                            onChangeText={(val) => this.inputValueUpdate(val, 'cost_lot_no')}
                                                            style={{
                                                                borderWidth: 1, 
                                                                borderColor: nowTheme.COLORS.GRAY 
                                                            }}
                                                            color={nowTheme.COLORS.GRAY}
                                                        />
                                                    </Block>
                                                </Block>
                                            </Block>

                                            <Block style={{paddingBottom: 10}}>
                                                <Block>
                                                    <Block>
                                                        <Text
                                                            title={'Weld Process (R/H)'}
                                                            size={15}
                                                            bold
                                                            color={nowTheme.COLORS.GRAY}
                                                        />
                                                    </Block>
                                                </Block>

                                                <Block>
                                                    <SelectMultiple 
                                                        selectedValue={this.state.Process_RH}
                                                        selectedItem={this.selectedItem}
                                                        removeSelected={this.removeSelected}
                                                        textChanged={this.textChanged}
                                                        data={this.state.listItems}
                                                        categorySelected={this.state.categorySelected}
                                                        category={'Process_RH'}
                                                        placeholder={'Weld Process (R/H)'}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{paddingBottom: 10}}>
                                                <Block>
                                                    <Block>
                                                        <Text
                                                            title={'Weld Process (F/C)'}
                                                            size={15}
                                                            bold
                                                            color={nowTheme.COLORS.GRAY}
                                                        />
                                                    </Block>
                                                </Block>

                                                <Block>
                                                    <SelectMultiple 
                                                        selectedValue={this.state.Process_FC}
                                                        selectedItem={this.selectedItem}
                                                        removeSelected={this.removeSelected}
                                                        textChanged={this.textChanged}
                                                        // data={this.state.listItems}
                                                        data={this.state.listItems}
                                                        categorySelected={this.state.categorySelected}
                                                        category={'Process_FC'}
                                                        placeholder={'Weld Process (F/C)'}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{paddingBottom: 10}}>
                                                <Block>
                                                    <Block>
                                                        <Text
                                                            title={'Welder ID (R/H)'}
                                                            size={15}
                                                            bold
                                                            color={nowTheme.COLORS.GRAY}
                                                        />
                                                    </Block>
                                                </Block>

                                                <Block>
                                                    <SelectMultiple 
                                                        selectedValue={this.state.ID_RH}
                                                        selectedItem={this.selectedItem}
                                                        removeSelected={this.removeSelected}
                                                        textChanged={this.textChanged}
                                                        // data={this.state.listItems}
                                                        data={this.state.listItems}
                                                        categorySelected={this.state.categorySelected}
                                                        category={'ID_RH'}
                                                        placeholder={'Weld ID (R/H)'}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{paddingBottom: 10}}>
                                                <Block>
                                                    <Block>
                                                        <Text
                                                            title={'Welder ID (F/C)'}
                                                            size={15}
                                                            bold
                                                            color={nowTheme.COLORS.GRAY}
                                                        />
                                                    </Block>
                                                </Block>

                                                <Block>
                                                    <SelectMultiple 
                                                        selectedValue={this.state.ID_FC}
                                                        selectedItem={this.selectedItem}
                                                        removeSelected={this.removeSelected}
                                                        textChanged={this.textChanged}
                                                        // data={this.state.listItems}
                                                        data={this.state.listItems}
                                                        categorySelected={this.state.categorySelected}
                                                        category={'ID_FC'}
                                                        placeholder={'Weld ID (F/C)'}
                                                    />
                                                </Block>
                                            </Block>

                                            <Block style={{paddingBottom: 10}}>
                                                <Block>
                                                    <Block>
                                                        <Text
                                                            title={'WPS (R/H)'}
                                                            size={15}
                                                            bold
                                                            color={nowTheme.COLORS.GRAY}
                                                        />
                                                    </Block>
                                                </Block>

                                                <Block>
                                                    <SelectMultiple 
                                                        selectedValue={this.state.WPS_RH}
                                                        selectedItem={this.selectedItem}
                                                        removeSelected={this.removeSelected}
                                                        textChanged={this.textChanged}
