    onSubmit = async() => {
        var progress    = this.state.progress;

        var data = [];

        // ============ WELD PROCESS (R/H) =============
        var process_rh = [];

        this.state.Process_RH.forEach(element => {
            process_rh.push(element.id);
        });
        // =============================================

        // ============ WELD PROCESS (F/C) =============
        var process_fc = [];

        this.state.Process_FC.forEach(element => {
            process_fc.push(element.id);
        });
        // =============================================

        // ============== WELDER ID (R/H) ==============
        var id_rh = [];

        this.state.ID_RH.forEach(element => {
            id_rh.push(element.id);
        });
        // =============================================

        // ============== WELDER ID (F/C) ==============
        var id_fc = [];

        this.state.ID_FC.forEach(element => {
            id_fc.push(element.id);
        });
        // =============================================

        // ================ WPS (R/H) ==================
        var wps_rh = [];

        this.state.WPS_RH.forEach(element => {
            wps_rh.push(element.id);
        });
        // =============================================

        // ================ WPS (F/C) ==================
        var wps_fc = [];

        this.state.WPS_FC.forEach(element => {
            wps_fc.push(element.id);
        });
        // =============================================

        var api         = this.state.dataLogin.api;

        data.push({
            'process_rh': process_rh,
            'process_fc': process_fc,
            'id_rh': id_rh,
            'id_fc': id_fc,
            'wps_rh': wps_rh,
            'wps_fc': wps_fc,
            'cost_lot_no': this.state.cost_lot_no,
            'remarks': this.state.remarks,
            'location': this.state.Location,
        })

        let options = {
            method: 'POST',
            headers: new Headers({
                'Authorization': api,
                'Content-Type': 'application/json'
            }),
            body: JSON.stringify({
                // approved_by: id_user,
                submission: data,
                progress: progress,
                menu: this.state.menu,
                item: this.state.item,
                data_workpack: this.state.data_workpack, 
                data_user: this.state.dataLogin, 
            })
        };

        var url = config.config.url + '/api/wp/surveyor_submit_process';

        console.log(url)
        console.log(api)
        console.log(options)

        await fetch(url, options)
        .then(response => response.text())
        .then(data => {

            console.log(data);

            this.setState({
                successAlert: true,
                listApproval: []
            });

            this.props.navigation.navigate('VT Category');

        })
        .catch(error => {
            console.error(error);
        });
    }

    selectChanged = (item) => {
        this.setState({
            progress: item
        });
    }

    selectedItem = (item, category) => {
        var data = this.state[category];
            data.push(item);

        this.setState({
            [category] : data,
            listItems: [],
        });

        console.log(this.state.WPS);
    }

    selectedItemAC = (item, category) => {
        this.setState({
            [category] : item,
            listItems: [],
        });

        console.log(this.state.WPS);
    }

    removeSelected = (item, category) => {
        var data = this.state[category];

        for(var i=0; i < data.length; i++){
            if(data[i].id == item.id){
                data.splice(i, 1);
            }
        }

        this.setState({
            [category] : data,
            listItems: [],
        });
    }

    textChanged = (val, props) => {
        var search      = val;
        var category    = props;
        var project     = this.state.dataLogin.project;
        var workpack_no = this.state.workpack_no;
        var api         = this.state.dataLogin.api;

        if(search){
          var url = '';

          if(category == 'Process_RH' || category == 'Process_FC'){
            url = config.config.url + '/api/weld_process/' + project + '/' + search;
          } else if(category == 'Fitter_Code'){
            url = config.config.url + '/api/fitter_code/' + project + '/' + search;
          } else if(category == 'ID_RH' || category == 'ID_FC'){
            url = config.config.url + '/api/tack_weld_id/' + project + '/' + search;
          } else if(category == 'WPS_RH' || category == 'WPS_FC'){
            url = config.config.url + '/api/wps/' + project + '/' + search;
          } else if(category == 'Location'){
            url = config.config.url + '/api/location/' + project + '/' + search;
          }

          console.log(url);
          console.log(api);

          fetch(url, {
              method: 'GET',
              headers: new Headers({
                  'Authorization': api
              }),
          })
          .then((response) => response.text())
          .then(data =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
              list_data = data + '}';
            }

            list_data = JSON.parse(list_data);

            this.setState({
              listItems: list_data.results,
              categorySelected: category,
            })

          }).catch((error) => {
            console.error(error);
          });

        } else {
          this.setState({
            listItems: []
          });
        }

    }

    renderContent = (item, index) => {
        return(
            <ListChecked 
                itemChecked={this.itemChecked}
                item={item}
                key={index}
                status={item.selected}
            />
        )
    }

    submitClicked = () => {
        var list_data = this.state.listSubmit;

        this.props.navigation.navigate('Workpack Piecemark Submission', {
            id: list_data,
        })
    }

    itemChecked = (checked, item) => {
        var id = item.id;

        let submit = this.state.listSubmit;

        if(checked == true){
            submit.push(id);
        } else {
            for(var i=0; i < submit.length; i++){
                if(submit[i] == id){
                    submit.splice(i, 1);
                }
            }
        }

        this.setState({listSubmit: submit});

        console.log(this.state.listSubmit);

    }

    searchClicked = () => {
        var workpack_id = this.state.Workpack_No.id;
        var workpack_no = this.state.Workpack_No.name;
        var phase = this.state.Workpack_No.phase;

        if(phase == 'PF'){
            this.props.navigation.navigate('Workpack Piecemark', {
                title: workpack_no,
                id: workpack_id,
            })
        } else {
            this.props.navigation.navigate('Workpack Joint', {
                title: workpack_no,
                id: workpack_id,
            })
        }

    }
}

