    onSubmit = async() => {
        var progress    = this.state.progress;

        var data = [];

        // ================= FITTER CODE ===============
        var fitter_code = [];

        this.state.Fitter_Code.forEach(element => {
            fitter_code.push(element.id);
        });
        // =============================================

        // ================ TACK WELD ID ===============
        var welder_code = [];

        this.state.Tack_Weld_ID.forEach(element => {
            welder_code.push(element.id);
        });
        // =============================================

        // =================== WPS =====================
        var wps = [];

        this.state.WPS.forEach(element => {
            wps.push(element.id);
        });
        // =============================================

        var api         = this.state.dataLogin.api;

        data.push({
            'fitter_code': fitter_code,
            'welder_code': welder_code,
            'wps': wps,
        })

        var id_master   = this.state.id_master;
        // var id_user     = this.state.dataLogin.id_user;
        // var api         = this.state.dataLogin.api;

        let options = {
            method: 'POST',
            headers: new Headers({
                'Authorization': api,
                'Content-Type': 'application/json'
            }),
            body: JSON.stringify({
                // approved_by: id_user,
                submission: data,
                progress: progress,
                menu: this.state.menu,
                id: this.state.item.id,
                id_master: id_master, 
                data_user: this.state.dataLogin, 
            })
        };

        var url = config.config.url + '/api/wp/surveyor_submit_process';

        console.log(url)
        console.log(api)
        console.log(options)

        await fetch(url, options)
        .then(response => response.text())
        .then(data => {

            console.log(data);

            this.setState({
                successAlert: true,
                listApproval: []
            });

            // this.props.navigation.navigate('Detail MV');

        })
        .catch(error => {
            console.error(error);
        });
    }

    selectChanged = (item) => {
        this.setState({
            progress: item
        });
    }

    selectedItem = (item, category) => {
        var data = this.state[category];
            data.push(item);

        this.setState({
            [category] : data,
            listItems: [],
        });

        console.log(this.state.WPS);
    }

    removeSelected = (item, category) => {
        var data = this.state[category];

        for(var i=0; i < data.length; i++){
            if(data[i].id == item.id){
                data.splice(i, 1);
            }
        }

        this.setState({
            [category] : data,
            listItems: [],
        });
    }

    textChanged = (val, props) => {
        var search      = val;
        var category    = props;
        var project     = this.state.dataLogin.project;
        var workpack_no = this.state.workpack_no;
        var api         = this.state.dataLogin.api;

        if(search){
          var url = '';

          if(category == 'Process_RH'){
            url = config.config.url + '/api/process_rh/' + project + '/' + workpack_no + '/' + search;
          } else if(category == 'Fitter_Code'){
            url = config.config.url + '/api/fitter_code/' + project + '/' + search;
          } else if(category == 'Tack_Weld_ID'){
            url = config.config.url + '/api/tack_weld_id/' + project + '/' + search;
          } else if(category == 'WPS'){
            url = config.config.url + '/api/wps/' + project + '/' + search;
          }

          console.log(url);
          console.log(api);

          fetch(url, {
              method: 'GET',
              headers: new Headers({
                  'Authorization': api
              }),
          })
          .then((response) => response.text())
          .then(data =>{

            var list_data = data;

            if(data.slice(-1) != '}'){
              list_data = data + '}';
            }

            list_data = JSON.parse(list_data);

            this.setState({
              listItems: list_data.results,
              categorySelected: category,
            })

          }).catch((error) => {
            console.error(error);
          });

        } else {
          this.setState({
            listItems: []
          });
        }

    }

