import React from "react";

import { 
    ActivityIndicator,
    AsyncStorage,
    Dimensions,
    FlatList,
    StyleSheet, 
    ScrollView,
    Keyboard,
    TouchableWithoutFeedback,
    View,
} from "react-native";

import { 
    Block, 
    Input, 
    theme, 
} from "galio-framework";

import { 
    GradientHeader, 
    DetailMVList, 
    Button, 
    Filter, 
    Icon, 
    LoadMore,
    Text, 
} from "../../components";

import { nowTheme, detail_mrir, config } from "../../constants";

import category from "../../constants/VT/category_vt";

import { LinearGradient } from 'expo-linear-gradient';

const { height, width } = Dimensions.get("screen");

var iconColor = '#6FBE6F';
var gradientColor = ['#0BAB64', '#83D475'];

const DismissKeyboard = ({ children }) => (
    <TouchableWithoutFeedback onPress={() => Keyboard.dismiss()}>{children}</TouchableWithoutFeedback>
);

class Detail extends React.Component {

    _isMounted = false;

    constructor() {
        super();
        console.log();

        this.state = {
          listData: [],
          isLoading: true,
          isLoadData: false,
          loadButton: false,
          data_user: [],
          filter: '',
          title: '',
        };
    }

    async componentDidMount() {
        this._isMounted = true;

        var data_user = await AsyncStorage.getItem('data_user');
        var data_user = JSON.parse(data_user);

        this.setState({
            data_user: data_user,
            title: this.props.route.params.title,
        });

        // this.readData()

        // console.log(this.state.title);

        return () => { isMounted = false };
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    render() {

        return (
            <DismissKeyboard>
                <Block>
                    { this.renderContent() }
                </Block>
            </DismissKeyboard>
        );
    }

    renderContent = () => {
        return(
            <Block center>
                <FlatList
                    data={category}
                    keyExtractor={(category, index) => index.toString()}
                    renderItem={(category) => {
                        return(
                            this.renderItem(category)
                        )
                    }}
                />

            </Block>

        )
    }

    renderItem = ({item}) => {

        var link = item.action;

        return(
          <TouchableWithoutFeedback
            onPress={() => this.props.navigation.navigate(link, {
              title: item.title + ' - ' + item.fullname,
              menu: item.menu,
            })}
          >
            <Block style={{ paddingTop: 20}}>
              <Block 
                center
                style={{ 
                  borderRadius: 8,
                  paddingVertical: 20,
                  backgroundColor: nowTheme.COLORS.WHITE,
                  width: width / 1.1,
                  height: height * 0.17,
                }}
              >
                <Block>
                  <Icon
                    size={50}
                    color={iconColor}
                    name={item.icon}
                    family={item.family}
                  />
                </Block>

                <Block style={{paddingTop: 5, justifyContent: 'center', alignItems: 'center', flex: 1}}>
                  <Text
                    style={{ fontFamily: 'montserrat-bold', textAlign: 'center' }}
                    size={17}
                    color={nowTheme.COLORS.GRAY}
                    title={item.title}
                    bold={true}
                  />
                </Block>
              </Block>
            </Block>
          </TouchableWithoutFeedback>
        )
    }
}

const styles = StyleSheet.create({
  home: {
    width: width
  },
  articles: {
    width: width - theme.SIZES.BASE * 2,
    paddingVertical: theme.SIZES.BASE,
    paddingHorizontal: 2,
    fontFamily: 'montserrat-regular'

  }
});

export default Detail;
